}

display ForecastItemAllocationId defaultForecastItemAllocation(SalesQuotationLine _salesQuotationLine)
{
    ForecastItemAllocationId    forecastItemAllocationId;

    if (_salesQuotationLine.ProjTransType == QuotationProjTransType::Item &&
        !_salesQuotationLine.ItemId)
    {
        forecastItemAllocationId = ProjParameters::find().DefaultForecastItemAllocation;
    }

    return forecastItemAllocationId;
}

