public void lookup()
{
    SysTableLookup sysTableLookup = element.addProjTableFieldsForLookup(this);
    Query query = element.buildProjIdQueryForLookp();

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

public boolean modified()
{
    boolean ret;

    ProjTable   projTable;

    ret = super();

    projTable = ProjTable::find(existingProjId.text());

    if (!projTable)
    {
        ret = checkFailed(strFmt("@SYS11217", existingProjId.text()));
    }

    if (ret && existingProjId.text())
    {
        existingProjInvoiceProjId.text(projTable.ProjInvoiceProjId);
    }

    if (ret && !element.valid_QuotationProjType(projTable.Type))
    {
        ret = checkFailed("@SYS98421");
    }

    if (ret && projTable.Status == ProjStatus::Completed)
    {
        ret = checkFailed("@SYS98422");
    }

    if (ret && projTable.CustAccount != sysWizard.parmSalesQuotationTable().CustAccount && sysWizard.parmSalesQuotationTable().CustAccount)
    {
        ret = checkFailed("@SYS98449");
    }

    if (sysWizard.parmSalesQuotationTable().ProjInvoiceProjId && projTable.ProjInvoiceProjId != sysWizard.parmSalesQuotationTable().ProjInvoiceProjId)
    {
        ret = checkFailed("@SYS100397");
    }

    if (ret
        && sysWizard.parmSalesQuotationTable().isMarkedForBankLG()
        && projTable.BankLGDocumentMap::isLinkedToGuarantee())
    {
        // The project with a Letter of Guarantee created is not allowed.
        ret = checkFailed("@SYS325158");
        this.text('');
    }
    isFixedPriceProject = element.isProjTypeFixedPrice(projTable);

    element.setNext();

    return ret;
}

public boolean validate()
{
    #define.project('Project')
    boolean ret;

    ret = super();

    if (ret && ctrlProjId.text())
    {
        ret = checkFailed(strFmt("@SYS31784", ctrlProjId.text(), #project, tableId2name(tableNum(ProjTable))));
    }

    return ret;
}

