public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret && !element.valid_QuotationProjType(newProjType.selection()))
    {
        ret = checkFailed("@SYS98421");
    }

    if (ret)
    {
        element.changeType();
    }

    if (! ProjTable::isNumberSeqManual())
    {
        ctrlProjId.allowEdit(false);
    }

    projTable_ProjGroupId.text("");
    isFixedPriceProject = element.checkProjTypeIsFixedPrice();;

    element.setNext();

    return ret;
}

