public boolean validate()
{
    boolean ret = true;

    if (this.text())
    {
        ret = ProjGroup::checkExist(this.text());
        if (ret && enum2str(ProjGroup::find(this.text()).ProjType) != newProjType.valueStr())
        {
            ret = checkFailed("@SYS55607");
        }
    }

    if (ret)
    {
        ret = super();
    }

    return ret;
}

