/// </param>
/// <remarks>
///     Extend this method to add or remove fields from Project Contract lookups.
/// </remarks> 
protected void addProjInvoiceTableFieldsForLookup(SysTableLookup _sysTableLookup)
{
    _sysTableLookup.addLookupfield(fieldNum(ProjInvoiceTable, ProjInvoiceProjId), true);
    _sysTableLookup.addLookupfield(fieldNum(ProjInvoiceTable, Description));

    _sysTableLookup.addLookupMethod(tableMethodStr(ProjInvoiceTable, fundingSourceName));
    _sysTableLookup.setLabel("@SYS27874");
}

