void clicked()
{
    Args args;

    if (sysWizard.parmSalesQuotationTable())
    {
        args = new Args(element);
        args.parm(custAccount.text());
        args.record(sysWizard.parmSalesQuotationTable());
        this.menufunction().run(args);
    }
    else
    {
        this.parameters(custAccount.text());
        super();
    }
}

