/// <summary>
/// Updates project quotation lines if prompt was confirmed by user
/// and any of the header fields with impact on the order lines have changed.
/// </summary>
protected void updateSalesQuotationLinesWithPrompt()
{
    if (salesQuotationToLineUpdatePrompt)
    {
        salesQuotationToLineUpdatePrompt.runOperation();
        if (salesQuotationToLineUpdatePrompt.wasLineUpdated())
        {
            salesQuotationLine_DS.research();
        }
        salesQuotationToLineUpdatePrompt = null;
    }
}

public boolean canClose()
{
    boolean  ret;

    ret = super();

    if (element.closedCancel() && ret)
    {
        ret = salesQuotationTableForm.canClose(salesQuotationTable);
    }

    return ret;
}

public boolean canSubmitToWorkflow()
{
    boolean ret=false;

    if (isConfigurationkeyEnabled(configurationKeyNum(Project))
        && (salesQuotationTable.QuotationStatus == SalesQuotationStatus::Created
        ||  salesQuotationTable.QuotationStatus == SalesQuotationStatus::Revised)
        && SalesQuotationLine::exist(salesQuotationTable.QuotationId))
    {
        ret = true;
    }

    return ret;
}

void  changeType()
{
    if (salesQuotationTableType)
    {
        salesQuotationTableType.formMethodQuotationTypeModifyPreChange(element, salesQuotationTable_ds, true);
    }

    salesQuotationTableType = salesQuotationTable.type();

    salesQuotationTableType.formMethodQuotationTypeModifyPostChange(element, salesQuotationTable_ds, true);
}

public void close()
{
    xSysLastValue::saveLast(salesQuotationTableForm);

    if (salesQuotationTableType)
    {
        salesQuotationTableType.formMethodClose();
    }

    if (kittingFormHandler)
    {
        kittingFormHandler.formClose();
    }

    super();
}

public Common docCursor()
{
    Common docCursor;

    docCursor = super();

    if (docCursor.TableId == tableNum(InventDim))
    {
        docCursor = salesQuotationLine;
    }

    return docCursor;
}

void doRefresh()
{
    salesQuotationTable_ds.reread();
    salesQuotationTable_ds.refresh();
    element.setButtonAccessHeader();

    salesQuotationLine_ds.research(true);
}

boolean  editHeaderAllowed()
{
    boolean  editHeader = true;

    if (!salesQuotationTable.checkUpdate(false))
    {
        editHeader = false;
    }

    return editHeader;
}

boolean  editLineAllowed()
{
    boolean  editLine = true;

    if (!salesQuotationTable.checkUpdate(false))
    {
        editLine = false;
    }

    return editLine;
}

void  editQuotation(boolean  allowEdit)
{
    #SysSystemDefinedButtons

    salesQuotationTable_ds.allowCreate  (!lockFieldsForProjectQuotations);
    salesQuotationTable_ds.allowEdit    (allowEdit && !lockFieldsForProjectQuotations);
    salesQuotationTable_ds.allowDelete  (salesQuotationTable.checkDelete() && !lockFieldsForProjectQuotations);
    salesQuotationLine_ds.allowEdit     (allowEdit && !lockFieldsForProjectQuotations);
    salesQuotationLine_ds.allowCreate   (allowEdit && !lockFieldsForProjectQuotations);
    salesQuotationLine_ds.allowDelete   (salesQuotationLine.checkDelete() && !lockFieldsForProjectQuotations);

    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, ProjInvoiceProjId)).allowEdit(true);

    FormCommandButtonControl editButton = this.control(this.controlId(#SystemDefinedViewEditButton)) as FormCommandButtonControl;
    editButton.enabled(salesQuotationTable_ds.allowEdit());
}

public void enableLinePCActions()
{
    SalesQuotationTableInteractionHelper salesQuotationTableInteractionHelper = SalesQuotationTableInteractionHelper::construct();

    salesQuotationTableInteractionHelper.initLinePCActions(salesQuotationLine);

    pcExecute.enabled(salesQuotationTableInteractionHelper.parmPCExecuteEnabled());
}

/// <summary>
/// Get the <c>LogisticsPostalAddressTransactionFormHandler</c> instance.
/// </summary>
/// <param name = "_parameter">The string parameter on the menu item used to call the form part.</param>
/// <returns>A <c>LogisticsPostalAddressTransactionFormHandler</c> instance.</returns>
public LogisticsPostalAddressTransactionFormHandler getTransactionAddressFormHandler(str _parameter)
{
    LogisticsPostalAddressTransactionFormHandler addressHandler = new LogisticsPostalAddressTransactionFormHandler(element);

    // There are three LogisticsPostalAddressTransaction controls on the form that will call into this method,
    // one for the header, one for line, one for header address on the line view.
    switch (_parameter)
    {
        case LogisticsPostalAddressTransactionFormHandler::HeaderAddress:
            addressHandler.parmTransactionFormDataSource(SalesQuotationTable_ds);
            addressHandler.parmAddressNameFieldId(fieldNum(SalesQuotationTable, DeliveryName));
            addressHandler.parmPostalAddressFieldId(fieldNum(SalesQuotationTable, DeliveryPostalAddress));
            addressHandler.parmNewAddressButtonMenuItemName(menuItemDisplayStr(DirPartyPostalAddressNew));
            break;

        case LogisticsPostalAddressTransactionFormHandler::LineAddress:
            addressHandler.parmTransactionFormDataSource(SalesQuotationLine_ds);
            addressHandler.parmAddressNameFieldId(fieldNum(SalesQuotationLine, DeliveryName));
            addressHandler.parmPostalAddressFieldId(fieldNum(SalesQuotationLine, DeliveryPostalAddress));
            addressHandler.parmNewAddressButtonMenuItemName(menuItemDisplayStr(DirPartyPostalAddressNew));
            break;

        case LogisticsPostalAddressTransactionFormHandler::HeaderInLineViewAddress:
            addressHandler.parmTransactionFormDataSource(SalesQuotationTable_ds);
            addressHandler.parmAddressNameFieldId(fieldNum(SalesQuotationTable, DeliveryName));
            addressHandler.parmPostalAddressFieldId(fieldNum(SalesQuotationTable, DeliveryPostalAddress));
            addressHandler.parmNewAddressButtonMenuItemName(menuItemDisplayStr(DirPartyPostalAddressNew));
            addressHandler.parmShowAddressField(false);
            break;
    }

    return addressHandler;
}

/// <summary>
/// Finds the addresses that are associated with the current transaction record.
/// </summary>
/// <returns>A <c>container</c> of address <c>RecId</c>, which are associated with the header and lines.</returns>
public container getInUseAddresses()
{
    container inUseAddresses;

    inUseAddresses = LogisticsLocationSelectionLookup::transactionAssociatedAddresses(
        tableNum(SalesQuotationLine), fieldNum(SalesQuotationLine, DeliveryPostalAddress),
        fieldNum(SalesQuotationLine, QuotationId), salesQuotationTable.QuotationId,
        salesQuotationTable.DeliveryPostalAddress);

    return inUseAddresses;
}

/// <summary>
/// Get the record of the party entity associated with the current transaction record.
/// </summary>
/// <returns></returns>
public Common getEntity()
{
    Common common;

    if (salesQuotationTable.CustAccount)
    {
        common = CustTable::find(salesQuotationTable.CustAccount);
    }
    else
    {
        common = smmBusRelTable::find(salesQuotationTable.BusRelAccount);
    }

    return common;
}

void gotoLines()
{
    salesQuotationLineProjGrid.setFocus();
}

public void init() 
{
    Common              callerRecord;
    smmBusRelTable      busRelTable;
    boolean             enableLGFeature;

    super();

    quotationType = QuotationType::Project;

    if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
    {
        salesAvailableDlvDatesHeader.visible(false);
    }
    else
    {
        activity.visible(false);
    }

    callerRecord = element.args().record();
    if (callerRecord.TableId == tableNum(smmBusRelTable))
    {
        busRelTable = callerRecord;
        if (busRelTable.BusRelTypeId == smmBusRelTypeGroup::getCustomerType())
        {
            callerRecord = CustTable::findByPartyRecId(busRelTable.Party);
        }
    }

    salesQuotationTableForm = SalesQuotationTableForm::construct(quotationType, callerRecord);

    TaxVATNumTable::enableLookupVATNum(vatNum);

    element.updateDesign(InventDimFormDesignUpdate::Init);

    salesQuotationLineProjGrid.visibleColsValue(8+conLen(inventDimFormSetup.dimFieldsActive(inventDimFormSetup.parmDimParmVisibleGrid())));

    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, QuotationType)).allowEdit(salesQuotationTableForm.editQuotationType());

    salesQuotationTable_ds.query(salesQuotationTableForm.querySalesQuotationTable(salesQuotationTable_ds.query()));
    salesQuotationLine_ds.query(salesQuotationTableForm.querySalesQuotationLine(salesQuotationLine_ds.query()));

    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, CashDiscPercent)).visible(false);

    crmLicense = smmLicense::crm();
    busRelTypeId.visible(crmLicense);

    // Init for Bank document
    bankLGFeatureChecker    = BankLGFeatureChecker::construct();
    enableLGFeature         = bankLGFeatureChecker.checkFeatureEnabled();
    bankLGRequestForm.visible(enableLGFeature);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, BankDocumentType)).visible(enableLGFeature);

    // <GTE>
    if (TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
    {
        TaxDocumentLauncher.visible(true);
        TransTaxInformation.visible(true);
        TaxGroup.visible(false);
        LineSalesTax_TaxGroup.visible(false);
        LineSalesTax_TaxItemGroup.visible(false);
        TaxTransSource.visible(false);
    }
    // </GTE>

    isKittingEnabled = FeatureStateProvider::IsFeatureEnabled(KittingFeature::instance());

    if (isTaxIntegrationEnabledForProject)
    {
        salesQuotationTableForm.controlTaxIntegrationFieldsForTableAndLine(SalesQuotationTable_ds, SalesQuotationLine_ds);
    }
}

