/// <summary>
///    Determines if the form has been invoked by another form.
/// </summary>
/// <returns>
///    true if the form has been invoked from another form; otherwise, false.
/// </returns>
public boolean isCalledFromOtherPage()
{
    Object      myCaller;
    boolean     calledFromOtherPage;

    if (element.args())
    {
        myCaller = element.args().caller();
        if (myCaller && myCaller is FormRun)
        {
            calledFromOtherPage = true;
        }
    }
    return calledFromOtherPage;
}

