/// <summary>
/// Sets the accessbility of bank document field(s).
/// </summary>
public void setBankDocumentFieldAccess()
{
    boolean enableDocumentType;

    if (bankLGFeatureChecker.checkFeatureEnabled())
    {
        enableDocumentType = salesQuotationTable.type().mayBankDocumentTypeBeModified();
        salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, BankDocumentType)).allowEdit(enableDocumentType);
    }
}

void setButtonAccessHeader()
{
    container                   existingJournals;
    boolean                     isNotTemplate = !salesQuotationTable.isTemplate();
    boolean                     isCustomer = salesQuotationTable && salesQuotationTable.CustAccount;

    taxTransSource.enabled(isNotTemplate);
    contactPerson.enabled(isNotTemplate);
    printMgmt.enabled(isNotTemplate);
    priceDiscActual.enabled(isNotTemplate);

    if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
    {
        boolean wbsEstimatesExist = salesQuotationTable.checkExistWBSEstimates();
        if (!wbsEstimatesExist)
        {
            psaGenerateQuotationLines.enabled(false);
        }
        else
        {
            psaGenerateQuotationLines.enabled(salesQuotationTable.QuotationStatus == SalesQuotationStatus::Created ||
                                              salesQuotationTable.QuotationStatus == SalesQuotationStatus::Revised ||
                                              salesQuotationTable.QuotationStatus == SalesQuotationStatus::Modified);
        }

        psaEstimatedDate.allowEdit(!wbsEstimatesExist);
    }

    existingJournals = salesQuotationTable.existingJournals();

    buttonJournalQuotation.enabled          (conPeek(existingJournals, SalesQuotationTableType::posQuotation())      ? true : false);
    custQuotationJournalConfirmation.enabled(conPeek(existingJournals, SalesQuotationTableType::posConfirmation())   ? true : false);

    salesQuotationTableForm.enableFunctionButtons(salesQuotationTable,
                                                  buttonConvert2Customer,
                                                  buttonAlternativeQuotations,
                                                  buttonCopyAllHeader);

    salesQuotationTableForm.enableUpdateButtons(salesQuotationTable,
                                                updateQuotation_Proj,
                                                buttonUpdateConfirmation,
                                                buttonUpdateLost,
                                                buttonUpdateCancelled);

    reviseQuotation.enabled((salesQuotationTable.QuotationStatus == SalesQuotationStatus::Sent || salesQuotationTable.QuotationStatus == SalesQuotationStatus::Approved) && !lockFieldsForProjectQuotations);

    salesQuotationTableForm.enableUpdateButtonsProject(salesQuotationTable,
                                                       buttonLinkAndTransferToProject);

    customer.enabled(isCustomer);
    businessRelationship.enabled(salesQuotationTable && salesQuotationTable.BusRelAccount);

    SalesPriceHour.enabled(isCustomer);
    SalesPriceExpense.enabled(isCustomer);
    SalesPriceFee.enabled(isCustomer);
    SalesPriceSubscription.enabled(isCustomer);

    activity.enabled(!lockFieldsForProjectQuotations);

    element.setBankDocumentButtonAccess();
}

public void setButtonAccessLine()
{
    SalesQuotationStatus quotationStatus = salesQuotationLine.salesQuotationTable().QuotationStatus;

    actionSalesQuotationProjCopyAllLines.enabled(quotationStatus == SalesQuotationStatus::Confirmed    ||
                                           quotationStatus == SalesQuotationStatus::Lost         ||
                                           (isConfigurationkeyEnabled(configurationKeyNum(Project)) ?
                                           quotationStatus == SalesQuotationStatus::Submitted : false)   ||
                                           quotationStatus == SalesQuotationStatus::Cancelled ? false : true);

    if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
    {
        salesQuotationLineDelete.enabled(quotationStatus != SalesQuotationStatus::Submitted &&
                                         quotationStatus != SalesQuotationStatus::Approved);
    }

    if (salesQuotationLine.ProjTransType == QuotationProjTransType::Item && ! salesQuotationTable.isTemplate())
    {
        // line action strip
        actionSuppItemCalc_Quotation.enabled(true);
        actionInventory.enabled(true);
        actionProductAndSupply.enabled(true);
        element.enableLinePCActions();
        actionUpdateLine.enabled(true);
        actionInventDimParmFixed.enabled(true);
        if (salesQuotationLine.ItemId)
        {
            actionBOMPriceCalc.enabled(salesQuotationLine.inventTable().inventItemType().canHaveBOM());
        }
    }
    else
    {
        // line action strip
        actionSuppItemCalc_Quotation.enabled(false);
        actionInventory.enabled(false);
        actionProductAndSupply.enabled(false);
        actionUpdateLine.enabled(false);
        actionInventDimParmFixed.enabled(false);
        actionBOMPriceCalc.enabled(false);
    }
}

/// <summary>
/// Enables or disables editing of the item and sales category fields.
/// </summary>
/// <param name="allowEdit">
/// Whether to enable editing.
/// </param>
void setEditItemOrCategoryAllowed(boolean  allowEdit)
{
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, ItemId)).allowEdit(salesQuotationLine.ProjTransType == QuotationProjTransType::Item && allowEdit);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, SalesCategory)).allowEdit(salesQuotationLine.ProjTransType == QuotationProjTransType::Item && allowEdit);
}

void  setEditProjTransTypeFieldsAllowed(boolean  allowEdit)
{
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, ProjTransType)).allowEdit(allowEdit);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, ProjectResource)).allowEdit(salesQuotationLine.ProjTransType != QuotationProjTransType::None && salesQuotationLine.ProjTransType != QuotationProjTransType::Item && allowEdit);

    boolean isFeeTransaction = salesQuotationLine.ProjTransType == QuotationProjTransType::Fee;
    // Disable editing of cost fields for revenue transaction type
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, CostPrice)).allowEdit(!isFeeTransaction);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, PriceUnit)).allowEdit(!isFeeTransaction);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, QtyOrdered)).allowEdit(!isFeeTransaction);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, SalesQty)).allowEdit(!isFeeTransaction);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, SalesUnit)).allowEdit(!isFeeTransaction);
}

public void setFieldAccessHeader()
{
    boolean     showTemplateFields  = true;
    boolean     isCustomer;

    isCustomer = salesQuotationTable.CustAccount ? true : false;

    if (crmLicense)
    {
        salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, BusRelAccount)).allowEdit(salesQuotationTable.QuotationStatus != SalesQuotationStatus::Sent);
    }

    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, InvoiceAccount)).allowEdit(isCustomer);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, ProjIdRef)).allowEdit(isCustomer);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, CustAccount)).allowEdit(salesQuotationTable.QuotationStatus != SalesQuotationStatus::Sent);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, ProjInvoiceProjId)).allowEdit(isCustomer);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, vatNum)).allowEdit(isCustomer);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, InvoiceAccount)).allowEdit(isCustomer);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, PriceGroupId)).allowEdit(isCustomer);

    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, TransferredToForecast)).allowEdit(salesQuotationTable.QuotationStatus != SalesQuotationStatus::Sent);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, TransferredToItemReq)).allowEdit(salesQuotationTable.QuotationStatus != SalesQuotationStatus::Sent);

    if (this.canTemplateFieldsBeModified())
    {
        showTemplateFields  = false;
    }

    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, TemplateActive)).allowEdit(salesQuotationTable.TemplateName ? true : false);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, TemplateGroupId)).allowEdit(showTemplateFields);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, TemplateName)).allowEdit(showTemplateFields);

    element.setBankDocumentFieldAccess();
}

/// <summary>
/// Determines if template fields can be modified - template fields can not be modified if status <> created or if quotation lines exist
/// </summary>
/// <returns>true if the template fields can be modified; otherwise, false.</returns>
protected boolean canTemplateFieldsBeModified()
{
    return (salesQuotationTable.QuotationStatus != SalesQuotationStatus::Created) 
        || SalesQuotationLine::exist(salesQuotationTable.QuotationId);
}

public void setFieldAccessLine()
{
    boolean inventoriedItem = salesQuotationLine.isStocked();

    if (salesQuotationLine.ProjTransType == QuotationProjTransType::Item)
    {
        barcode.enabled(true);
        foreignTrade1.enabled(true);
        itemRouteId.enabled(true);
        projItem.enabled(true);

        tabLineDelivery.enabled(true);
        tabLineProduct.enabled(true);
        tabLinePacking.enabled(true);
        tabLineForeignTrade.enabled(true);

        deliveryDate.enabled(true);
        miscDeliveryInfoLine.enabled(true);
        projQuantity_PriceUnit.enabled(true);
        if (!salesQuotationLine.isItemOrNoneAndCategoryBased() && salesQuotationLine.isStocked())
        {
            projCostAmount_CostPrice.enabled(false);
        }
        else
        {
            projCostAmount_CostPrice.enabled(true);
        }
    }
    else
    {
        barcode.enabled(false);
        foreignTrade1.enabled(false);
        itemRouteId.enabled(false);

        tabLineDelivery.enabled(false);
        tabLineProduct.enabled(false);
        tabLinePacking.enabled(false);
        tabLineForeignTrade.enabled(false);
        projItem.enabled(false);

        deliveryDate.enabled(false);
        miscDeliveryInfoLine.enabled(false);
        projQuantity_PriceUnit.enabled(false);
        projCostAmount_CostPrice.enabled(true);
    }
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, ProjCategoryId)).allowEdit(salesQuotationLine.ProjTransType != QuotationProjTransType::None);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, SalesPrice)).allowEdit(salesQuotationLine.ProjTransType != QuotationProjTransType::None);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, LineAmount)).allowEdit(salesQuotationLine.ProjTransType != QuotationProjTransType::None);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, TransDate)).allowEdit(salesQuotationLine.ProjTransType != QuotationProjTransType::None);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, ProjDescription)).allowEdit(salesQuotationLine.ProjTransType != QuotationProjTransType::None);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, LinePropertyId)).allowEdit(salesQuotationLine.ProjTransType != QuotationProjTransType::None);
}

void setTaxWithholdEnabled_IN()
{
    salesQuotationLineTaxExtensionIN_ds.object(fieldNum(SalesQuotationLineTaxExtensionIN, tdsGroup)).enabled((!SalesQuotationLineTaxExtensionIN.tcsGroup));
    salesQuotationLineTaxExtensionIN_ds.object(fieldNum(SalesQuotationLineTaxExtensionIN, tcsGroup)).enabled((!SalesQuotationLineTaxExtensionIN.tdsGroup));
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    InventMovement movement = InventMovement::construct(salesQuotationLine);

    movement.quotationType(quotationType);

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init        :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_Mov::newFromFormMovement(element, movement);
            }
        case InventDimFormDesignUpdate::Active      :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(),movement);
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(),movement);
            inventDimFormSetup.formSetControls(false);
            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

/// <summary>
/// Hides or shows delivery controls outside action pane.
/// </summary>
public void hideDeliveryControls()
{
    boolean isConfirmed = salesQuotationTable.QuotationStatus == SalesQuotationStatus::Confirmed;

    salesAvailableDlvDatesHeader.enabled(!isConfirmed);
}

/// <summary>
/// Rereads the <c>SalesQuotationLine</c> instance data.
/// </summary>
/// <param name = "_salesQuotationLine">A sales quotation line.</param>
protected void reReadSalesQuotationLine(SalesQuotationLine _salesQuotationLine)
{
    QuotationProjTransType projTransType = _salesQuotationLine.ProjTransType;
    ProjCategoryId projCategoryId        = _salesQuotationLine.ProjCategoryId;
    ItemId itemId                        = _salesQuotationLine.ItemId;

    salesQuotationTableForm.resetSalesQuotationLine(_salesQuotationLine);

    _salesQuotationLine.ProjTransType    = projTransType;
    _salesQuotationLine.ProjCategoryId   = projCategoryId;
    _salesQuotationLine.ItemId           = itemId;

    if (kittingFormHandler && kittingFormHandler.isDynKit(SalesQuotationLine))
    {
        return;
    }

    _salesQuotationLine.itemIdChanged();
    salesQuotationLine_ds.changedInventoriedStatus();

    // Reread ReferenceGroup controls, e.g. to show the category name.
    salesQuotationLine_ds.rereadReferenceDataSources();

    _salesQuotationLine.LinePropertyId = ProjLinePropertySetup::findLinePropertyId(_salesQuotationLine.salesQuotationTable().ProjIdRef,
                                                                                          _salesQuotationLine.ProjCategoryId);
    _salesQuotationLine.TaxItemGroup = ProjParameters::taxItemGroupItem(_salesQuotationLine.ProjCategoryId,
                                                                                 _salesQuotationLine.ItemId);
}

/// <summary>
/// Rereads sales quotation line dimensions data.
/// </summary>
/// <param name = "_salesQuotationLine">A sales quotation line.</param>
/// <param name = "_inventDim">An inventory dimension record.</param>
protected void reReadSalesQuotationLineDimensionsData(SalesQuotationLine _salesQuotationLine, InventDim _inventDim)
{
    _inventDim.data(InventDim::find(_salesQuotationLine.InventDimId));
}

/// <summary>
/// Rereads sales quotation line data in order to show the name.
/// </summary>
/// <param name = "_salesQuotationLine">A sales quotation line.</param>
protected void reReadSalesQuotationLineName(SalesQuotationLine _salesQuotationLine)
{
    InventTable inventTable = inventTable::find(_salesQuotationLine.ItemId);
    _salesQuotationLine.ProjDescription = inventTable.productName(salesQuotationTable.LanguageId, _salesQuotationLine.InventDimId);
    _salesQuotationLine.initName();

    if (kittingFormHandler && kittingFormHandler.isKit())
    {
        kittingFormHandler.setLineAccess(SalesQuotationLine_DS, InventDim_DS);
    }
}

private boolean isProjectExternallyManaged(ProjTable _projTable)
{
    boolean ret = false;

    if (cdsLockingOptionProjectQuotations)
    {
        if (Project::isProjectExternallyManaged(_projTable.ProjId))
        {
            ret = true;
        }
    }
    return ret;
}

/// <summary>
/// Customizes the options used to populate the Office Menu.
/// </summary>
/// <param name = "_menuOptions">The menu options to be customized.</param>
public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    // Clear the menu options which are driven by the root datasource of an entity as some of the options are valid for project quotation types.
    ListIterator dataEntityIterator = new ListIterator(_menuOptions.dataEntityOptions());

    while (dataEntityIterator.more())
    {
        OfficeMenuDataEntityOptions dataEntityOptions = dataEntityIterator.value();
        if (dataEntityOptions.dataEntityName() == tableStr(SalesQuotationHeaderEntity) || dataEntityOptions.dataEntityName() == tableStr(SalesQuotationHeaderV2Entity))
        {
            dataEntityIterator.delete();
        }
        else
        {
            dataEntityIterator.next();
        }
    }

    DocuTemplate docuProjQuotationHeaderTemplate = DocuTemplate::findTemplate(OfficeAppApplicationType::Excel, resourceStr(ProjQuotationHeaderExcelTemplate));
    DocuTemplate docuProjQuotationLineTemplate = DocuTemplate::findTemplate(OfficeAppApplicationType::Excel, resourceStr(ProjQuotationLineExcelTemplate));

    if (docuProjQuotationHeaderTemplate)
    {
        OfficeTemplateExportMenuItem menuItem = OfficeTemplateExportMenuItem::constructWithDocuTemplate(docuProjQuotationHeaderTemplate, docuProjQuotationHeaderTemplate.TemplateID);
        _menuOptions.customMenuItems().addEnd(menuItem);
    }

    if (docuProjQuotationLineTemplate)
    {
        OfficeTemplateExportMenuItem menuItem = OfficeTemplateExportMenuItem::constructWithDocuTemplate(docuProjQuotationLineTemplate, docuProjQuotationLineTemplate.TemplateID);
        _menuOptions.customMenuItems().addEnd(menuItem);
    }
}

/// <summary>
///     Verifies that the project can be selected for quotation.
/// </summary>
/// <param name = "_projTable">
///     An instance of <c>ProjTable</c> table buffer.
/// </param>
/// <returns>
///     true if the project type is neither time and material nor fixed price; otherwise, false.
/// </returns>
protected boolean validateProjTypeForProjIdRef(ProjTable _projTable)
{
    return _projTable.Type != ProjType::TimeMaterial && _projTable.Type != ProjType::FixedPrice && !(element.enableProjMultipleContractLinesForProjectFeature && _projTable.ProjInvoiceProjId);
}

/// <summary>
///     Constructs a query to create a lookup for <c>ProjIdRef</c> control.
/// </summary>
/// <returns>
///     An instance of query to construct lookup for <c>ProjIdRef</c> control.
/// </returns>
/// <remarks>
///      Extend this method to modify criteria on Project lookups.
/// </remarks>
protected Query buildProjIdRefQueryForLookup()
{
    Query query = new Query();
    QueryBuildDataSource qbsProjTable = query.addDataSource(tableNum(ProjTable));

    qbsProjTable.addRange(fieldNum(ProjTable,Status)).value(strFmt('!%1',queryValue(ProjStatus::Completed)));

    if (!element.enableProjMultipleContractLinesForProjectFeature)
    {
        qbsProjTable.addRange(fieldNum(ProjTable,Type)).value(strFmt('%1,%2', queryValue(ProjType::TimeMaterial), queryValue(ProjType::FixedPrice)));
    }

    if (cdsLockingOptionProjectQuotations)
    {
        qbsProjTable.addRange(fieldNum(ProjTable, ProjDataSource)).value(SysQuery::value(0));
    }

    if (salesQuotationTable.CustAccount)
    {
        qbsProjTable.addRange(fieldNum(ProjTable,CustAccount)).value(salesQuotationTable.CustAccount);
    }

    return query;
}

/// <summary>
///      Defines the default fields for Project lookups.
/// </summary>
/// <param name = "_formControl">
///     An instance of <c>FormControl</c> class.
/// </param>
/// <returns>
///     An instance of <c>SysTableLookup</c> which holds the lookup fields.
/// </returns>
/// <remarks>
///     Extend this method to add or remove fields from Project lookups.
/// </remarks>
protected SysTableLookup addProjIdRefFieldsForLookup(FormControl _formControl)
{
    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(ProjTable), _formControl, true);

    sysTableLookup.addLookupfield(fieldNum(ProjTable, ProjId), true);
    sysTableLookup.addLookupfield(fieldNum(ProjTable, Name));
    sysTableLookup.addLookupfield(fieldNum(ProjTable, ProjInvoiceProjId));

    return sysTableLookup;
}

