    this.editQuotation();
}

int  active()
{
    int                         ret;
    SalesQuotationTableType     editSalesQuotationTableType = salesQuotationTable.type();

    ret = super();

    cdsLockingOptionProjectQuotations = ProjCDSLockingOptionTable::get(ProjCDSLockingOption::ProjectQuotations);
    isExternallyManagedProject = Project::isProjectExternallyManaged(salesQuotationTable.ProjIdRef);
    lockFieldsForProjectQuotations = cdsLockingOptionProjectQuotations && isExternallyManagedProject;

    element.editQuotation(element.editHeaderAllowed());
    salesQuotationTable_ds.rereadReferenceDataSources();

    salesQuotationTableForm.active(salesQuotationTable);

    element.setButtonAccessHeader();
    element.setFieldAccessHeader();

    if (salesQuotationTable.checkUpdate(false))
    {
        salesQuotationChangeReasonCode.enabled(false);
    }
    else
    {
        salesQuotationChangeReasonCode.enabled(true);
    }

    return ret;
}

display str getTitle(SalesQuotationTable _record)
{
    str ret;

    if (_record.CustAccount)
    {
        // Customer is defined...
        ret = strFmt("@Proj:SalesQuotationTableTitleFmt", _record.QuotationId,
        _record.CustAccount,
        _record.customerName());
    }
    else
    {
        // Business relation is defined...
        ret = strFmt("@Proj:SalesQuotationTableTitleFmt", _record.QuotationId,
        _record.BusRelAccount,
        _record.busRelName());
    }

    return ret;
}

void  editQuotation()
{
    element.editQuotation(element.editHeaderAllowed());
}

void  create(boolean  append = true)
{
    SalesQuotationTable     newSalesQuotationTable;

    element.editQuotation(true);

    salesQuotationTableForm.salesQuotationTable(salesQuotationTable);

    if (salesQuotationTableForm.create())
    {
        newSalesQuotationTable = salesQuotationTableForm.salesQuotationTable();

        if (newSalesQuotationTable)
        {
            super(append);

            salesQuotationTable.data(newSalesQuotationTable);
            salesQuotationTable_ds.rereadReferenceDataSources();
            salesQuotationTable_ds.research(true);
        }
    }
    else
    {
        if (element.isCalledFromOtherPage())
        {
            element.close();
        }
        else
        {
            this.editQuotation();
        }
    }
}

void  delete()
{
    if (salesQuotationTable.checkDelete())
    {
        if (salesQuotationTableType)
        {
            salesQuotationTableType.formMethodDataSourceDelete(element, salesQuotationTable_ds);
        }

        HierarchyIdBase wbsQuotationHierarchyId = HierarchyLinkTable::findRefTableRecId(tableNum(SalesQuotationTable), salesQuotationTable.RecId).HierarchyId;

        super();

        if (wbsQuotationHierarchyId)
        {
            ProjPlanVersionsManager::deleteWBSByHierarchyId(wbsQuotationHierarchyId);
        }
    }
}

void linkActive()
{
    smmBusRelTable      smmBusRelTable;
    CustTable           custTable;
    DirPartyRecId       party;
    QuotationIdBase     quotationId;

    switch (element.args().dataset())
    {
        // Is the form called from the activities form
        case tableNum(smmActivities) :
            smmActivityParentLink::updateQuery(this.query().dataSourceTable(tableNum(SalesQuotationTable)), element.args().record());
            break;

        // Is form called from call list target
        case tableNum(smmTMCallListTable) :
            party = element.args().record().(fieldNum(smmTMCallListTable,Party));
            break;

        // Is form called from campaign target
        case tableNum(smmCampaignSelection) :
            party = element.args().record().(fieldNum(smmCampaignSelection,Party));
            break;

        // Is form called from smmBusRelTable
        case tableNum(smmBusRelTable) :
            party = element.args().record().(fieldNum(smmBusRelTable,Party));
            break;
    }

    if (party)
    {
        this.query().dataSourceTable(tableNum(SalesQuotationTable)).clearDynalinks();
        custTable = CustTable::findByPartyRecId(party);
        if (custTable)
        {
            this.query().dataSourceTable(tableNum(SalesQuotationTable)).addDynalink(fieldNum(SalesQuotationTable,CustAccount),custTable,fieldNum(CustTable,AccountNum));
        }
        else
        {
            smmBusRelTable = smmBusRelTable::findByParty(party);
            this.query().dataSourceTable(tableNum(SalesQuotationTable)).addDynalink(fieldNum(SalesQuotationTable,BusRelAccount),smmBusRelTable,fieldNum(smmBusRelTable,BusRelAccount));
        }
    }

    if (salesQuotationTableForm.linkActive())
    {
        salesQuotationTableForm.salesQuotationTable(element.args().record());
        salesQuotationTable_ds.query(salesQuotationTableForm.querySalesQuotationTable(salesQuotationTable_ds.query()));
    }

    if (salesQuotationTableType)
    {
        salesQuotationTableType.formMethodDataSourceLinkActive(element, salesQuotationTable_ds);
    }

    super();

    quotationId = salesQuotationTableForm.getQuotationIdFromCaller(element.args().caller());
    if (quotationId && quotationId != salesQuotationTable.QuotationId)
    {
        salesQuotationTable_ds.positionToRecordByValue(fieldNum(SalesQuotationTable, QuotationId),quotationId);
    }
}

void  write()
{
    #OCCRetryCount
    DialogButton                    copyAddress;
    boolean                         convertCurrency = false;
    boolean                         linesExist      = false;
    TradeCurencyConversion          tradeCurencyConversion = TradeCurencyConversion::ExchRate;
    smmActivities                   localSmmActivities;

    if (salesQuotationTable.checkUpdate())
    {
        linesExist = SalesQuotationLine::exist(salesQuotationTable.QuotationId);
        if (linesExist || MarkupTrans::exist(salesQuotationTable.TableId, salesQuotationTable.RecId))
        {
            if (salesQuotationTable.CurrencyCode != salesQuotationTable.orig().CurrencyCode)
            {
                [convertCurrency, tradeCurencyConversion] = SalesQuotationTable::promptConvertCurrencyCode(salesQuotationTable);
            }
        }

        if (linesExist)
        {
            if (this.needCopyAddress())
            {
                copyAddress = SalesQuotationTable::promptCopyDeliveryAddress();
            }
        }

        if (salesQuotationTable.BusRelAccount)
        {
            salesQuotationTable.updateFromAdvPromptActivity(salesQuotationTable.orig());
        }

        try
        {
            ttsbegin;

            if (copyAddress != DialogButton::Cancel  &&  copyAddress != DialogButton::No)
            {
                salesQuotationTable.copyAddressToLine(copyAddress, salesQuotationTable);
            }

            if (convertCurrency)
            {
                salesQuotationTable.convertCurrencyCode(salesQuotationTable.CurrencyCode, tradeCurencyConversion, salesQuotationTable.ProjIdRef);
            }

            salesQuotationTable.Touched = NoYes::No;

            if (salesQuotationTableType)
            {
                salesQuotationTableType.formMethodDataSourceWrite(element, salesQuotationTable_ds);
            }

            if (copyTaxGroupToMarkupTrans == DialogButton::Yes || copyTaxGroupToMarkupTrans == DialogButton::YesToAll)
            {
                MarkupTrans::updateTaxGroup(salesQuotationTable.TableId, salesQuotationTable.RecId, salesQuotationTable.TaxGroup, salesQuotationTable.orig().TaxGroup, copyTaxGroupToMarkupTrans);
                copyTaxGroupToMarkupTrans = DialogButton::No;
            }

            super();

            element.updateSalesQuotationLinesWithPrompt();

            ttscommit;
        }
        catch (Exception::Deadlock)
        {
            retry;
        }
        catch (Exception::UpdateConflict)
        {
            if (appl.ttsLevel() == 0)
            {
                if (xSession::currentRetryCount() >= #RetryNum)
                {
                    throw Exception::UpdateConflictNotRecovered;
                }
                else
                {
                    retry;
                }
            }
            else
            {
                throw Exception::UpdateConflict;
            }
        }
        catch (Exception::Error)
        {
            if (salesQuotationTable.QuotationFollowupActivity
                && salesQuotationTable.orig().QuotationFollowupActivity != salesQuotationTable.QuotationFollowupActivity)
            {
                ttsbegin;
                localSmmActivities = smmActivities::find(salesQuotationTable.QuotationFollowupActivity,true);
                if (localSmmActivities)
                {
                    localSmmActivities.delete(false);
                }
                ttscommit;
            }
        }

        salesQuotationTableType = null;
    }
}

boolean validateWrite()
{
    boolean         ret;

    ret = super();

    if (ret && salesQuotationTableType && !salesQuotationTableType.formMethodDataSourceValidateWrite(element, salesQuotationTable_ds))
    {
        ret = false;
    }

    copyTaxGroupToMarkupTrans = DialogButton::No;
    if (ret && salesQuotationTable.TaxGroup != salesQuotationTable.orig().TaxGroup)
    {
        copyTaxGroupToMarkupTrans = MarkupTrans::promptCopyTaxGroupToMarkupTrans(salesQuotationTable.TableId, salesQuotationTable.RecId);
        ret = copyTaxGroupToMarkupTrans != DialogButton::Cancel;
    }

    if (ret)
    {
        salesQuotationToLineUpdatePrompt = SalesQuotationToLineUpdatePrompt::newExecutePrompt(salesQuotationTable, copyTaxGroupToMarkupTrans);
        // If the salesQuotationToLineUpdatePrompt object is null no fields are to be updated.
        if (salesQuotationToLineUpdatePrompt)
        {
            ret = salesQuotationToLineUpdatePrompt.parmExtendedPromptReturnValue();
        }
    }

    return ret;
}

public void reRead()
{
    if (salesQuotationTableType)
    {
        salesQuotationTableType.formMethodDataSourceDelete(element, salesQuotationTable_ds, true);
    }

    super();

    // status may have changed, reset buttons, fields
    element.editQuotation(element.editHeaderAllowed());
}

void setTouched()
{
    // required by form MarkupTrans to communicate changes to the caller
    salesQuotationTableForm.setTouched(salesQuotationTable);
}

