{
    QueryBuildDataSource queryBuildDataSource = salesQuotationTable_ds.query().dataSourceTable(tableNum(SalesQuotationTable));

    SysQuery::findOrCreateRange(queryBuildDataSource,fieldNum(SalesQuotationTable, QuotationType)).value(enum2str(QuotationType::Project));
}

