{
    this.initRanges();

    super();
}

/// <summary>
/// Checks if quotation type is Project.
/// </summary>
/// <returns>
/// Returns true if quotation type is Project; otherwise, false.
/// </returns>
[Hookable(false)]
public boolean isProjectQuotation()
{
    return quotationType == QuotationType::Project;
}

public boolean validate()
{
    boolean ret;
    MCRsmmBusRelTable  mcRsmmBusRelTable;

    ret = super();

    if (ret && salesQuotationTable.OpportunityId)
    {
        // Changing either the business account or customer account will clear the opportunity ID. Do you want to continue?
        ret = ((Box::okCancel("@SYS112839",DialogButton::Cancel) == DialogButton::Ok ) ? true : false);
    }
    if (isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter)))
    {
        // switch to surviving business relation when the entered business relation has been merged and
        // notify user of switch.
        mcRsmmBusRelTable = MCRsmmBusRelTable::findAccount(salesQuotationTable.BusRelAccount);
        if (mcRsmmBusRelTable.MergedRoot)
        {
            checkFailed(strFmt("@MCR12337",salesQuotationTable.BusRelAccount, mcRsmmBusRelTable.MergedRoot));
            salesQuotationTable.BusRelAccount = mcRsmmBusRelTable.MergedRoot;
        }

        if (mcRsmmBusRelTable.IsListDeleted)
        {
            ret = checkFailed("@MCR26132");
        }
    }

    return ret;
}

public void modified()
{
    smmQuotationAlternativeQuotations   smmQuotationAlternativeQuotations;

    if (crmLicense)
    {
        if (smmQuotationAlternativeQuotations::exist(salesQuotationTable.QuotationId))
        {
            if (Box::yesNo("@SYS90096" + " " + "@SYS41157", DialogButton::No, "@SYS23828", "@SYS23828") == DialogButton::Yes)
            {
                ttsbegin;

                delete_from smmQuotationAlternativeQuotations
                    where smmQuotationAlternativeQuotations.QuotationId == salesQuotationTable.QuotationId;
                ttscommit;
            }
            else
            {
                salesQuotationTable.BusRelAccount   = salesQuotationTable.orig().BusRelAccount;
            }
        }
        else if (smmQuotationAlternativeQuotations::existLinked(salesQuotationTable.QuotationId))
        {
            if (Box::yesNo("@SYS90096" + " " + "@SYS41157", DialogButton::No, "@SYS23828", "@SYS23828") == DialogButton::Yes)
            {
                ttsbegin;

                select forupdate smmQuotationAlternativeQuotations
                    where smmQuotationAlternativeQuotations.LinkedQuotationId == salesQuotationTable.QuotationId;
                smmQuotationAlternativeQuotations.delete();
                ttscommit;
            }
            else
            {
                salesQuotationTable.BusRelAccount   = salesQuotationTable.orig().BusRelAccount;
            }
        }
    }

    super();
    element.setFieldAccessHeader();
}

// To show lookup by code ( Business relations of type vendor & customer are filtered out)
public void lookup(FormControl _formControl, str _filterStr)
{
    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(smmBusRelTable), _formControl);
    QueryBuildDataSource            queryBuildDataSource;
    Query                           query;
    QueryBuildRange                 queryBuildRange;

    sysTableLookup.addLookupfield(fieldNum(smmBusRelTable, BusRelAccount),true);
    sysTableLookup.addLookupMethod(tableMethodStr(smmBusRelTable, name));
    sysTableLookup.addLookupfield(fieldNum(smmBusRelTable, BusRelTypeId));
    sysTableLookup.addLookupfield(fieldNum(smmBusRelTable, MainContactWorker));

    query = new Query();

    queryBuildDataSource = query.addDataSource(tableNum(smmBusRelTable));

    queryBuildDataSource = queryBuildDataSource.addDataSource(tableNum(smmBusRelTypeGroup));

    queryBuildDataSource.relations(true);

    queryBuildRange = queryBuildDataSource.addRange(fieldNum(smmBusRelTypeGroup, TableType));

    queryBuildRange.value(int2str(enum2int(smmBusRelTypeSourceTable::Relation)));

    sysTableLookup.parmQuery(query);

    sysTableLookup.performFormLookup();
}

