{
    super();

    salesQuotationTable_ds.refresh();
}

public boolean validate()
{
    boolean ret;
    CustTable   custTableLocal;

    ret = super();

    if (ret && salesQuotationTable.OpportunityId)
    {
        // Changing either the business account or customer account will clear the opportunity ID. Do you want to continue?
        ret = ((Box::okCancel("@SYS112839",DialogButton::Cancel) == DialogButton::Ok ) ? true : false);
    }
    if (isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter)))
    {
        // Switch to surviving BR when BR entered has been merged and notify user of switch.
        custTableLocal = CustTable::find(salesQuotationTable.CustAccount);
        if (custTableLocal.mcrMergedRoot)
        {
            checkFailed(strFmt("@MCR12337", salesQuotationTable.BusRelAccount, custTableLocal.mcrMergedRoot));
            salesQuotationTable.BusRelAccount = custTableLocal.mcrMergedRoot;
        }
    }

    return ret;
}

public void modified()
{
    smmQuotationAlternativeQuotations   smmQuotationAlternativeQuotations;

    if (crmLicense)
    {
        if (smmQuotationAlternativeQuotations::exist(salesQuotationTable.QuotationId))
        {
            if (Box::yesNo("@SYS90096" + " " + "@SYS41157",DialogButton::No,"@SYS23828","@SYS23828") == DialogButton::Yes)
            {
                ttsbegin;

                delete_from smmQuotationAlternativeQuotations
                    where smmQuotationAlternativeQuotations.QuotationId == salesQuotationTable.QuotationId;

                ttscommit;
            }
            else
            {
                salesQuotationTable.CustAccount     = salesQuotationTable.orig().CustAccount;
            }
        }
        else if (smmQuotationAlternativeQuotations::existLinked(salesQuotationTable.QuotationId))
        {
            if (Box::yesNo("@SYS90096" + " " + "@SYS41157",DialogButton::No,"@SYS23828","@SYS23828") == DialogButton::Yes)
            {
                ttsbegin;

                select forupdate smmQuotationAlternativeQuotations
                    where smmQuotationAlternativeQuotations.LinkedQuotationId == salesQuotationTable.QuotationId;
                smmQuotationAlternativeQuotations.delete();

                ttscommit;
            }
            else
            {
                salesQuotationTable.CustAccount     = salesQuotationTable.orig().CustAccount;
            }
        }

        if (salesQuotationTable.CustAccount != '' &&
            salesQuotationTable.CustAccount != salesQuotationTable.orig().CustAccount)
        {
            super();
            element.setFieldAccessHeader();
        }
    }
    else
    {
        super();
        element.setFieldAccessHeader();
    }
}

public void lookup(FormControl _formControl, str _filterStr)
{
    SalesQuotationTable::lookupOpportunityId(_formControl, _filterStr, salesQuotationTable.party());
}

public boolean validate()
{
    boolean         ret;
    ProjTable       projTable;

    ret = super();

    if (salesQuotationTable.ProjIdRef)
    {
        projTable = ProjTable::find(salesQuotationTable.ProjIdRef);

        if (ret && element.validateProjTypeForProjIdRef(projTable))
        {
            ret = checkFailed("@SYS98421");
        }

        if (ret && projTable.Status == ProjStatus::Completed)
        {
            ret = checkFailed("@SYS98422");
        }

        if (ret && projTable.CustAccount != salesQuotationTable.CustAccount && salesQuotationTable.CustAccount)
        {
            ret = checkFailed("@SYS98449");
        }

        if (ret && !projTable.status().checkRule(ProjStatusRule::CreateQuotation))
        {
            ret = checkFailed(strFmt("@SYS53010", projTable.Stage()));
        }

        if (ret && element.isProjectExternallyManaged(projTable))
        {
            ret = checkFailed(strfmt("@Proj:ProjCDSValueInvalidDueToExternal", "@Proj:ProjCDSProjectQuotations"));
        }
    }

    return ret;
}

public void lookup(FormControl _formControl, str _filterStr)
{
    SysTableLookup sysTableLookup = element.addProjIdRefFieldsForLookup(_formControl);
    Query query = element.buildProjIdRefQueryForLookup();

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

void  modified()
{
    super();

    element.changeType();
}

public void modified()
{
    super();

    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, TemplateActive)).allowEdit(salesQuotationTable.TemplateName ? true : false);
}

public boolean validate()
{
    boolean             ret;

    ret = super();

    if (ret && salesQuotationTable.isTemplate())
    {
        // Template name must be unique in a group
        if (SalesQuotationTable::existTemplate(salesQuotationTable.QuotationType,salesQuotationTable.TemplateGroupId, salesQuotationTable.TemplateName))
        {
            // Template name '%1' already exists in template group %2.
            ret = checkFailed(strFmt("@SYS99924", salesQuotationTable.TemplateName, salesQuotationTable.TemplateGroupId));
        }
    }

    return ret;
}

public void jumpRef()
{
    if (TaxVATNumTable::taxVATNumTableEnabled())
    {
        TaxVATNumTable::jumpRefVATNum(salesQuotationTable, element);
    }
    else
    {
        super();
    }
}

public void lookup(FormControl _formControl, str _filterStr)
{
    if (TaxVATNumTable::taxVATNumTableEnabled())
    {
        TaxVATNumTable::lookupVATNum(vatNum,salesQuotationTable);
    }
    else
    {
        super(_formControl, _filterStr);
    }
}

public Common resolveReference(FormReferenceControl _formReferenceControl)
{
    Common ret;

    ret = super(_formReferenceControl);

    if (salesQuotationTable_ds.isProjectQuotation() && ret.RecId && !ProjWorkerLookup::validateWorker(ret.RecId, salesQuotationTable, false))
    {
        ret.clear();
    }
    else if (!ret.RecId || !HcmWorkerLookup::newOnlyActiveWorkers().validateWorker(ret.RecId))
    {
        ret.clear();
    }

    return ret;
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    if (quotationType == QuotationType::Project)
    {
        return ProjWorkerLookup::lookupWorker(_formReferenceControl, salesQuotationTable, false);
    }

    return HcmWorkerLookup::newOnlyActiveWorkers().lookupWorker(_formReferenceControl);
}

public Common resolveReference(FormReferenceControl _formReferenceControl)
{
    Common ret;

    ret = super(_formReferenceControl);

    if (quotationType == QuotationType::Project && ret.RecId && !ProjWorkerLookup::validateWorker(ret.RecId, salesQuotationTable, false))
    {
        ret.clear();
    }
    else if (!ret.RecId || !HcmWorkerLookup::newOnlyActiveWorkers().validateWorker(ret.RecId))
    {
        ret.clear();
    }

    return ret;
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    if (quotationType == QuotationType::Project)
    {
        return ProjWorkerLookup::lookupWorker(_formReferenceControl, salesQuotationTable, false);
    }

    return HcmWorkerLookup::newOnlyActiveWorkers().lookupWorker(_formReferenceControl);
}

        /// <summary>
        /// Updates the form to reflect the current inventoried status inventoried status, i.e. after a change to the itemid, category, or inventoried field.
        /// </summary>
        void changedInventoriedStatus()
        {
            // Update inventDim with new data which has been set due to modification
            inventDim.data(InventDim::find(salesQuotationLine.InventDimId));

	        // Sets delivery date control type if it is not updated before.
            if (salesQuotationLine.orig().DeliveryDateControlType == salesQuotationLine.DeliveryDateControlType)
            {
				salesQuotationLine.setDeliveryDateControlType(salesQuotationLine.InventDimId);
            }

            InventMovement::bufferSetTransQtyUnit(salesQuotationLine);
            // Update inventdim fields
            element.updateDesign(InventDimFormDesignUpdate::FieldChange);

            // Disable category editing if itemid is selected
            salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, SalesCategory)).allowEdit(salesQuotationLine.ProjTransType == QuotationProjTransType::Item && salesQuotationLine.ItemId == '');
            // Disable itemid editing if only a category is selected
            salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, ItemId)).allowEdit(salesQuotationLine.ProjTransType == QuotationProjTransType::Item && !salesQuotationLine.isCategoryBased());

            // Update allow edit for inventory fields
            element.setFieldAccessLine();
        }

public void executeQuery()
{
    element.setButtonAccessLine();

    super();
}

void setTouched()
{
    // required by form MarkupTrans to communicate changes to the caller
    salesQuotationTableForm.setTouched(salesQuotationTable);
}

public boolean validateWrite()
{
    boolean                 ret;

    salesQuotationTableForm.setTouched(salesQuotationTable);
    salesQuotationLine.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;

    ret = super();

    if (ret)
    {
        salesQuotationTableForm.salesQuotationLine_ValidateWrite(salesQuotationLine, inventDim);
    }

    return ret;
}

        int  active()
        {
            int         ret;

            if (kittingFormHandler)
            {
                kittingFormHandler.DS_PreActive(this);
            }

            // <GIN>
            if (TaxWithholdParameters_IN::checkTaxParameters())
            {
                element.setTaxWithholdEnabled_IN();
            }
            // </GIN>

            ret = super();

            element.setEditItemOrCategoryAllowed(salesQuotationLine.RecId == 0);

            if (salesQuotationLine.RecId)
            {
                element.setEditProjTransTypeFieldsAllowed(false);
            }
            else
            {
                element.setEditProjTransTypeFieldsAllowed(true);
            }

            salesQuotationLine_ds.allowCreate(salesQuotationTableForm.createLineAllowed(salesQuotationLine) && !lockFieldsForProjectQuotations);
            salesQuotationLine_ds.allowEdit(salesQuotationTableForm.editLineAllowed() && !lockFieldsForProjectQuotations);
            salesQuotationLine_ds.allowDelete(salesQuotationTableForm.deleteLineAllowed(salesQuotationLine) && !lockFieldsForProjectQuotations);

            element.updateDesign(InventDimFormDesignUpdate::Active);

            element.setFieldAccessLine();
            element.setButtonAccessLine();

            if (kittingFormHandler)
            {
                kittingFormHandler.DS_Active(this, InventDim_ds);
            }

			// Refreshes the address line form part to load correct address data.
            DeliveryAddressLine.refresh();

            if (isTaxIntegrationEnabledForProject)
            {
                salesQuotationTableForm.controlTaxIntegrationFieldsForLine(salesQuotationLine);
            }

            return ret;
        }

void  create(boolean  append = true)
{
    element.setEditItemOrCategoryAllowed(true);
    element.setEditProjTransTypeFieldsAllowed(true);

    if (salesQuotationTable.checkUpdate())
    {
        super(append);
    }

    salesQuotationLine_ds.refresh();

    salesQuotationTableForm.salesQuotationLine_Create(salesQuotationLine);
    this.refresh();
}

/// <summary>
/// Delete current price references.
/// </summary>
protected void deleteCurrentPriceReference()
{
    salesQuotationLine.salesPurchLineInterface().cleanUpPriceHistoryRef();
}

void  delete()
{
    if (salesQuotationLine.checkDelete())
    {
        this.deleteCurrentPriceReference();

        super();

        if (kittingFormHandler)
        {
            kittingFormHandler.DS_Delete(this);
        }

        boolean mustFormResearchAfterDelete = salesQuotationTableForm.mustFormResearchAfterDelete(salesQuotationLine);
        if (mustFormResearchAfterDelete)
        {
            salesQuotationLine_ds.research(true);
        }

        SalesQuotationId quotationId = salesQuotationLine.QuotationId;
        if (quotationId && SalesQuotationTable::exist(quotationId))
        {
            salesQuotationTable_ds.reread();
            salesQuotationTable_ds.refresh();
        }

        element.setFieldAccessHeader();
        element.setButtonAccessLine();
        element.setButtonAccessHeader();
    }
}

void  write()
{
    // <GIN>
    #ISOCountryRegionCodes
    // </GIN>

    if (salesQuotationTable.checkUpdate())
    {
        salesQuotationTableForm.salesQuotationLine_Write(salesQuotationLine);

        // <GIN>
        salesQuotationLine.updateAndPackAccessableValue_IN(salesQuotationLine.orig(), salesQuotationLineTaxExtensionINOrig);
        // </GIN>

        boolean doKitResearch = kittingFormHandler && kittingFormHandler.mustResearch();
        if (kittingFormHandler)
        {
            ttsbegin;
            kittingFormHandler.DS_PreWrite(this);
        }

        super();

        if (kittingFormHandler)
        {
            kittingFormHandler.DS_Write(this, false);
            ttscommit;
        }

        if (salesQuotationLine.InventDimId != inventDim.InventDimId)
        {
            inventDim.data(InventDim::find(salesQuotationLine.InventDimId));
            inventDim_ds.setCurrent();
        }

        if (salesQuotationLine.isCategoryBased() && !salesQuotationLineForeignTradeCategory.SalesQuotationLineInventTransId)
        {
            salesQuotationLineForeignTradeCategory.initFromSalesQuotationLine(salesQuotationLine);
            salesQuotationLineForeignTradeCategory_ds.write();
        }

        salesQuotationTable_ds.reread();
        salesQuotationTable_ds.refresh();

        element.setFieldAccessHeader();
        element.setButtonAccessHeader();

        if (doKitResearch || (kittingFormHandler && kittingFormHandler.isKit()))
        {
            this.research(true);
        }
    }

    // <GIN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        if (salesQuotationLineTaxExtensionIN_ds.isNeeded())
        {
            TaxModelDocLineExtensionController_IN::insertTaxExtensionRecord(salesQuotationLineTaxExtensionIN, salesQuotationLine);
        }
    }
    // </GIN>
}

FormDataSource salesQuotationTable_ds()
{
    return salesQuotationTable_ds;
}

