public void modified()
{
    super();

    ItemFreeTxt name;

    if (keepName)
    {
        name = SalesQuotationLine.Name;
    }

    element.reReadSalesQuotationLine(SalesQuotationLine);

    element.reReadSalesQuotationLineDimensionsData(SalesQuotationLine, inventDim);

    InventMovement::bufferSetTransQtyUnit(SalesQuotationLine);

    element.reReadSalesQuotationLineName(SalesQuotationLine);

    if (keepName)
    {
        //Revert name field value to the saved value.
        SalesQuotationLine.Name = name;
    }
}

