public void modified()
{
    ItemFreeTxt name;
    boolean restoreName;

    //If the Item Text field already has the value then don't change the field value when project category changes
    if (keepName || SalesQuotationLine.Name)
    {
        //This variable is used to restore Name when either keepName is true or Name field is not empty
        restoreName = true;

        //Save the name field value.
        name = SalesQuotationLine.Name;
    }

    super();

    if (restoreName)
    {
        //Revert name field value to the saved value to restore it back to the same value as before
        SalesQuotationLine.Name = name;
    }
}

/// <summary>
/// Determines whether the input to the Resource field is a valid ResourceId or not.
/// </summary>
/// <param name = "_formReferenceControl">
/// The form control that is bound to the Resource field.
/// </param>
/// <returns>
/// The <c>HcmResource</c> record buffer that references the selected record.
/// </returns>
public Common resolveReference(FormReferenceControl _formReferenceControl)
{
    ResourceView ret;

    ret = super(_formReferenceControl);

    if (ret.RecId && !ResourceLookup::validateResource(ret.RecId, salesQuotationLine))
    {
        ret = null;
    }

    return ret;
}

/// <summary>
/// Opens the resource lookup.
/// </summary>
/// <param name = "_formReferenceControl">
/// The form control that is bound to the Resource field.
/// </param>
/// <returns>
/// The <c>HcmResource</c> record buffer that references the selected record.
/// </returns>
public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    return ResourceLookup::lookupResource(_formReferenceControl, salesQuotationLine, false);
}

public void modified()
{
    QuotationProjTransType      projTransType;

    super();

    projTransType = salesQuotationLine.ProjTransType;

    salesQuotationTableForm.resetSalesQuotationLine(salesQuotationLine);

    salesQuotationLine.ProjTransType = projTransType;

    // Set SalesQty to 1 for new lines if Expense, Hour, Fee
    if ((salesQuotationLine.ProjTransType != QuotationProjTransType::None) &&
        (salesQuotationLine.ProjTransType != QuotationProjTransType::Item) &&
        !(salesQuotationLine.RecId))
    {
        salesQuotationLine.SalesQty = 1;
    }

    // Initialy set to value in ProjParameter if item is selected
    if (projTransType == QuotationProjTransType::Item)
    {
        salesQuotationLine.ProjCategoryId = ProjParameters::find().ItemCategory;
        if (!SalesQuotationLine.ItemId)
        {
            SalesQuotationLine.ProjDescription = ProjCategory::find(salesQuotationLine.ProjCategoryId).Name;
        }
    }
    else
    {
        salesQuotationLine.setInventDimId(InventDim::inventDimIdBlank());
        inventDim.data(InventDim::find(salesQuotationLine.InventDimId));
        element.updateDesign(InventDimFormDesignUpdate::FieldChange);

        //
        //Clear values relating to projTransType of QuotationProjTransType::Item
        //

        // Barcode
        salesQuotationLine.BarCode = '';
        salesQuotationLine.BarCodeType = '';

        //Foreign Trade
        salesQuotationLine.TransactionCode = '';
        salesQuotationLine.Transport = '';
        salesQuotationLine.Port = '';
        salesQuotationLine.StatProcId = '';
        salesQuotationLine.StatTriangularDeal = 0;
        salesQuotationLine.CountyOrigDest = '';
        salesQuotationLine.IntrastatCommodity = 0;
        salesQuotationLine.OrigCountryRegionId = '';
        salesQuotationLine.OrigStateId = '';

        // BOM/Route
        salesQuotationLine.ItemBOMId = '';
        salesQuotationLine.ItemRouteId = '';

        // Delivery
        salesQuotationLine.OverDeliveryPct         = 0.0;
        salesQuotationLine.UnderDeliveryPct        = 0.0;
        salesQuotationLine.ReceiptDateRequested    = dateNull();
        salesQuotationLine.DeliveryDateControlType = SalesDeliveryDateControlType::None;
        salesQuotationLine.DlvMode = '';

        //Other
        salesQuotationLine.SalesCategory = 0;
    }

    element.setEditItemOrCategoryAllowed(true);
    element.setEditProjTransTypeFieldsAllowed(true);
    element.setFieldAccessLine();
}

public Common resolveReference(FormReferenceControl _formReferenceControl)
{
    return EcoResCategory::resolveCategoryHierarchyRole(
        _formReferenceControl,
        EcoResCategoryNamedHierarchyRole::Sales);
}

public void modified()
{
    QuotationProjTransType projTransType;
    SalesCategory salesCategory;
    ItemFreeTxt name;
    ProjCategoryId projCategoryId;

    // Name is defaulted from category in super(). So we save it if needed.
    if (keepName)
    {
        name = SalesQuotationLine.Name;
    }

    projTransType                       = SalesQuotationLine.ProjTransType;
    projCategoryId                      = SalesQuotationLine.ProjCategoryId;
    salesCategory                       = SalesQuotationLine.SalesCategory;
    salesQuotationTableForm.resetSalesQuotationLine(SalesQuotationLine);
    SalesQuotationLine.ProjTransType    = projTransType;
    SalesQuotationLine.ProjCategoryId   = projCategoryId;
    SalesQuotationLine.SalesCategory    = salesCategory;

    super();

    if (keepName)
    {
        //Revert name field value to the saved value.
        SalesQuotationLine.Name = name;
    }

    // Enabled/disable fields
    salesQuotationLine_ds.changedInventoriedStatus();

    if (SalesQuotationLine.isCategoryBased())
    {
        if (salesQuotationLineForeignTradeCategory.SalesQuotationLineDataAreaId == '')
        {
            //Only create a new record if no one exist
            salesQuotationLineForeignTradeCategory_ds.allowCreate(true);
            salesQuotationLineForeignTradeCategory_ds.create();
            salesQuotationLineForeignTradeCategory_ds.allowEdit(true);
            salesQuotationLineForeignTradeCategory_ds.allowCreate(false);
        }
    }
    else
    {
        salesQuotationLineForeignTradeCategory.clearFormFields();
        salesQuotationLineForeignTradeCategory_ds.allowEdit(false);
        salesQuotationLineForeignTradeCategory_ds.refresh();
    }
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    return EcoResCategory::lookupCategoryHierarchyRole(
        _formReferenceControl,
        EcoResCategoryNamedHierarchyRole::Sales);
}

