public void modified()
{
    super();

    salesQuotationLine.modifyInventDim(inventDim,fieldNum(InventDim,InventStyleId));
    salesQuotationLine.CostPrice = salesQuotationLine.inventTable().costPcsPrice(salesQuotationLine.InventDimId,inventDim);

    if (isKittingEnabled)
    {
        element.kittingInventDimFieldModified(inventDim_DS, fieldnum(InventDim, InventStyleId));
    }
}

public void modified()
{
    super();

    salesQuotationLine.modifyInventDim(inventDim,fieldNum(InventDim, InventVersionId));
    salesQuotationLine.CostPrice = salesQuotationLine.inventTable().costPcsPrice(salesQuotationLine.InventDimId, inventDim);
