Method.Source:
using Microsoft.Dynamics.Commerce.Headquarters.Instrumentation;

[Form]
public class SalesQuotationTable extends FormRun implements KittingIFormHandlerProvider
{
    #Retail
    boolean                         isCalledFromLP;
    SalesQuotationTableForm         salesQuotationTableForm;
    SalesQuotationTableType         salesQuotationTableType;
    QuotationType                   quotationType;
    SalesQuotationId                newQuotationId;
    boolean                         isRetailOrder;
    boolean                         retailOrderNeedRecalculation;
    boolean                         isRetailPricingDelayedCalculationEnabled;
    boolean                         isProductSearchLookupEnabled;

    private static readonly container DeliveryLineImageData = ImageReference::constructForSymbol(ImageReferenceSymbol::DeliveryLine).pack();
    private static readonly container OrderLineMultipleDeliveriesImageData = ImageReference::constructForSymbol(ImageReferenceSymbol::OrderLineMultipleDeliveries).pack();

    // Used for validateWrite and write to update tax groups on misc. charges
    DialogButton                    copyTaxGroupToMarkupTrans;

    InventDimCtrl_Frm_Mov           inventDimFormSetup;

    QueryBuildRange                 typeRange;

    boolean                         crmLicense;

