    boolean                         keepName; // When set to false Name is defaulted from category description.
    LogisticsLocationRecId          postalAddressLocation;

    BankLGFeatureChecker            bankLGFeatureChecker;

    MCRSourceID                     lastHeaderSourceId;

    InventDistinctProduct           productFromLookup;

    // Price override
    // Price override needs to save values off depending on whether it is a price override,
    // or the user is turning off an override.
    // These values will be used to write an order event.
    RetailInformationSubcodeId  retailInformationSubcodeIdLine;
    RetailInfocodeId            retailInfocodeIdLine;
    UserId                      mcrLinePriceOverrideUserId;
    MCROrderEventType           mcrLinePriceOverrideEventType;
    // Similar to line price override values above, but for header price
    // override.
    RetailInformationSubcodeId  retailInformationSubcodeIdHeader;
    UserId                      mcrHeaderPriceOverrideUserId;
    SalesEndDiscPct             mcrHeaderPriceOverrideDiscPct;
    MCROrderEventType           mcrHeaderPriceOverrideEventType;
