public void activate(boolean _active)
{
    super(_active);
    salesQuotationTable_ds.rereadReferenceDataSources();
}

void calculateTotalDiscount()
{
    salesQuotationTable.updateFinalDisc();

    if (salesQuotationTable.Touched)
    {
        salesQuotationTable_ds.reread();
        salesQuotationTable_ds.refresh();
        salesQuotationTable_ds.active();

        //This will trigger the creditlimit calculation when SalesQuotationTable.validateWrite is called
        salesQuotationTable_ds.setTouched();
    }
}

/// <summary>
/// Makes manual discount percentage field visible and disable <c>SalesQuotationLine</c> dicount fields.
/// </summary>
/// <param name="_enableDisableDiscountFields">
/// The flag to enable disable specific fields.
/// </param>
/// <remarks>
/// Use for retail quote.
/// </remarks>
protected void enableRetailFields(boolean _isRetailOrder)
{
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, LineDisc)).enabled(!_isRetailOrder);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, LinePercent)).enabled(!_isRetailOrder);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, PriceUnit)).enabled(!_isRetailOrder);
    AffiliationsButtonGroup.enabled(_isRetailOrder);
    retailSalesQuotationLine_ds.object(fieldNum(RetailSalesQuotationLine, LineManualDiscountPercentage)).enabled(_isRetailOrder);

    //By default this control is not editable
    SalesQuotationTable_RetailChannel.visible(_isRetailOrder);
}

/// <summary>
/// Enables change reason code.
/// </summary>
protected void enableChangeReasonCode()
{
    if (salesQuotationTable.checkUpdate(false))
    {
        salesQuotationChangeReasonCode.enabled(false);
    }
    else
    {
        salesQuotationChangeReasonCode.enabled(true);
    }
}

/// <summary>
/// Enables form controls on sales quotation active event.
/// </summary>
[Replaceable]
protected void enableControlsOnSalesQuotationActive()
{
    this.editQuotation(element.editHeaderAllowed());
    salesQuotationTable_ds.rereadReferenceDataSources();
    if (enableDirectedSelling)
    {
        lastHeaderSourceId = mcRsalesQuotationTable.SourceId;
    }

    this.showHeaderLogisticsPostalEffectiveAddressMessage();

    salesQuotationTableForm.active(salesQuotationTable);

    this.setFieldAccessHeader();

    this.enableChangeReasonCode();

    this.configBankDocumentControls();
    this.setTitleText();

    this.enableSalesQuotationFields_BR();
    this.enableRetailControls();
    this.enablePriceDiscControls();

    this.enableWorkFlowControls();

    SalesQuotationOwnershipChange.enabled(salesQuotationTable.canUpdateQuotationOwnership());
}

/// <summary>
/// Enables the workflow related controls.
/// </summary>
protected void enableWorkFlowControls()
{
    if (isRetailOrder && enableOrderPriceControl_Workflow)
    {
        workflowStatusIcon.visible(true);
        SalesQuotationLine_LineAmount.allowEdit(false);
        MCRPriceOverride.visible(true);
    }
    else
    {
        this.disableWorkflow();
    }
}

/// <summary>
/// Enables the Brazil specific fields on the sales quotation.
/// </summary>
protected void enableSalesQuotationFields_BR()
{
    if (BrazilParameters::isEnabled())
    {
        salesQuotationTable.enableDataSourceFields_BR();
    }
}

/// <summary>
/// Sets and enables retail variables and controls.
/// </summary>
protected void enableRetailControls()
{
    isRetailOrder = element.useRetailPricingEngine();
    this.enableRetailFields(isRetailOrder);
    retailOrderNeedRecalculation = false;

    RetailParameters retailParameters = RetailParameters::find();
    isRetailPricingDelayedCalculationEnabled = isRetailOrder && retailParameters.EnableDelayedPriceAndDiscountCalculation;
}

/// <summary>
/// Enables the price and discount controls.
/// </summary>
protected void enablePriceDiscControls()
{
    boolean isCustAccount = salesQuotationTable.CustAccount ? true : false;
    priceDiscTable_Sales.enabled(isCustAccount);
    salesPrice.enabled(isCustAccount);
    salesLineDisc.enabled(isCustAccount);
    salesMultiLineDisc.enabled(isCustAccount);
    salesEndDisc.enabled(isCustAccount);
    priceDiscAdmTable_SalesCreate.enabled(isCustAccount);
    suppItemTable_Sales.enabled(isCustAccount);
}

/// <summary>
/// Disables workflow on the form.
/// </summary>
[Replaceable]
protected void disableWorkflow()
{
    this.design().workflowEnabled(false);
}

public boolean canClose()
{
    boolean  ret;

    ret = super();

    if (element.closedCancel() && ret)
    {
        ret = salesQuotationTableForm.canClose(salesQuotationTable);
    }

    return ret;
}

void  changeType()
{
    if (salesQuotationTableType)
    {
        salesQuotationTableType.formMethodQuotationTypeModifyPreChange(element, salesQuotationTable_ds, true);
    }

    salesQuotationTableType = salesQuotationTable.type();

    salesQuotationTableType.formMethodQuotationTypeModifyPostChange(element, salesQuotationTable_ds, true);
}

void  checkCreditNoteLine()
{
    boolean creditNote = salesQuotationLine.creditNoteLine();

    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, CostPrice)).allowEdit(creditNote);
    salesQuotationLine_ds.object(fieldNum(SalesQuotationLine, CostPrice)).skip(!creditNote);
}

void checkEnableDiscount(boolean _isCustomer)
{
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, PriceGroupId)).allowEdit(_isCustomer);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, MultiLineDisc)).allowEdit(_isCustomer);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, EndDisc)).allowEdit(_isCustomer);
    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, LineDisc)).allowEdit(_isCustomer);
}

public void close()
{
    salesQuotationTableForm.isAdvanced(true);
    xSysLastValue::saveLast(salesQuotationTableForm);

    // Current price references may need to be deleted.
    salesQuotationLine.salesPurchLineInterface().cleanUpPriceHistoryRef();

    if (salesQuotationTableType)
    {
        salesQuotationTableType.formMethodClose();
    }

    if (kittingFormHandler)
    {
        kittingFormHandler.formClose();
    }

    super();
}

/// <summary>
///     Configs the BankDocument Controls.
/// </summary>
public void configBankDocumentControls()
{
    boolean enableDocumentType = false;

    if (bankLGFeatureChecker.checkFeatureEnabled())
    {
        enableDocumentType = salesQuotationTable.type().mayBankDocumentTypeBeModified();
        salesQuotationTable_DS.object(fieldNum(SalesQuotationTable, BankDocumentType)).allowEdit(enableDocumentType);
    }
}

public Common docCursor()
{
    Common docCursor;

    docCursor = super();

    if (docCursor.TableId == tableNum(InventDim))
    {
        docCursor = salesQuotationLine;
    }

    return docCursor;
}

void doRefresh()
{
    salesQuotationTable_ds.reread();
    salesQuotationTable_ds.refresh();

    salesQuotationLine_ds.reread();
    salesQuotationLine_ds.refresh();
    salesQuotationLine_ds.executeQuery();
}

boolean  editHeaderAllowed()
{
    boolean  editHeader = true;

    if (!salesQuotationTable.checkUpdate(false))
    {
        editHeader = false;
    }

    return editHeader;
}

public void editQuotation(boolean _allowEdit)
{
    salesQuotationTable_ds.allowEdit    (_allowEdit);
    salesQuotationTable_ds.allowDelete  (salesQuotationTable.checkDelete());
    salesQuotationLine_ds.allowEdit     (_allowEdit);
    salesQuotationLine_ds.allowCreate   (_allowEdit);
    salesQuotationLine_ds.allowDelete   (salesQuotationLine.checkDelete());

    this.enableSalesQuotationFields_BR();
}

public void enableHeaderUpdateActions()
{
    salesQuotationTableForm.enableUpdateButtonsMultiselect(
        salesQuotationTable,
        buttonUpdateQuotation,
        buttonUpdateConfirmation,
        buttonUpdateLost,
        buttonUpdateCancelled);

    if (SalesQuotationTableFormFunctionButtonFlight::instance().isEnabled())
    {
        salesQuotationTableForm.enableFunctionButtonsMultiselect(
            salesQuotationTable,
            buttonConvert2Customer,
            buttonAlternativeQuotations,
            buttonCopyAllHeader);
    }
}

public void enableLineInquiryActions(boolean enableActions)
{
    reqTransNet.enabled(enableActions);

    salesATP.enabled(enableActions);
}

public void enableLinePCActions()
{
    SalesQuotationTableInteractionHelper salesQuotationTableInteractionHelper = SalesQuotationTableInteractionHelper::construct();

    salesQuotationTableInteractionHelper.initLinePCActions(salesQuotationLine);

    pcExecute.enabled(salesQuotationTableInteractionHelper.parmPCExecuteEnabled());
}

void gotoLines()
{
    salesQuotationLineGrid.setFocus();
}

/// <summary>
/// Determine whether form quote must be updated based on opportunity status.
/// </summary>
/// <param name = "_opportunityStatus">Opportunity Status</param>
/// <returns>true if form quote must be updated based on opportunity status; otherwise, false.</returns>
protected boolean mustUpdateFormBasedOnOppurtunityStatus(smmOpportunityStatus _opportunityStatus)
{
    return !SmmOpportunityStatusUpdate::updateFromQuote(salesQuotationTable_ds, _opportunityStatus);
}

/// <summary>
/// Hides or shows delivery controls outside action pane.
/// </summary>
/// <remarks>
/// Hide/show logic cannot be moved to interaction class since interaction class is only suited for controls in action pane.
/// </remarks>
public void hideDeliveryControls()
{
    boolean isProspect = salesQuotationTable.BusRelAccount != '';
    boolean isConfirmed = salesQuotationTable.QuotationStatus == SalesQuotationStatus::Confirmed;
    boolean isIntegrationOwnerDynamics365Sales = salesQuotationTable.isIntegrationOwnerDynamics365Sales();

    lineViewDirPartyPostalAddressNewHeader.visible(!isProspect);
    lineViewBusRelDirPartyPostalAdrNewHeader.visible(isProspect);

    dirPartyPostalAddressNewLine.visible(!isProspect);
    busRelDirPartyPostalAddressNewLine.visible(isProspect);

    dirPartyPostalAddressNewHeader.visible(!isProspect);
    busRelDirPartyPostalAddressNewHeader.visible(isProspect);

    salesAvailableDlvDatesHeader.visible(!salesQuotationTable.isTemplate());

    lineViewDirPartyPostalAddressNewHeader.enabled(!isConfirmed);
    lineViewBusRelDirPartyPostalAdrNewHeader.enabled(!isConfirmed);

    dirPartyPostalAddressNewLine.enabled(!isConfirmed);
    busRelDirPartyPostalAddressNewLine.enabled(!isConfirmed);

    dirPartyPostalAddressNewHeader.enabled(!isConfirmed);
    busRelDirPartyPostalAddressNewHeader.enabled(!isConfirmed);

    salesAvailableDlvDatesLines.enabled(!isConfirmed && !isIntegrationOwnerDynamics365Sales);
    salesAvailableDlvDatesHeader.enabled(!isConfirmed && !isIntegrationOwnerDynamics365Sales);
    lineViewSalesAvailableDlvDatesLines.enabled(!isConfirmed && !isIntegrationOwnerDynamics365Sales);
}

public void init()
{
    Common          callerRecord = element.args().record();
    smmBusRelTable  busRelTable;
    boolean         enableLGFeature = false;
    boolean         enableConfigurationFeature = PCRuntimeLibrary::isConfigurationKeyEnabled();
    isProductSearchLookupEnabled = EcoResProductSearchLookup::isProductSearchLookupEnabled();
    correlationId = newGuid();

    salesParameters     = SalesParameters::find();
    custParameters      = CustParameters::find();
    mcrOrderParameters  = MCROrderParameters::find();

    // <GIN>
    #isoCountryRegionCodes
    // </GIN>

    if (element.args())
    {
        isCalledFromWorkSpace = element.args().callerName() == formStr(SysGenPinnedList);
    }

    if (TradeFormHelper::isCalledFromForm(element.args(), formStr(CustTable)))
    {
        if (!SalesParameters::find().IsSalesQuotationMenuItemManagerEnabled)
        {
            callerRecord = null;
        }
    }

    isCalledFromLP = TradeFormHelper::isCalledFromForm(element.args(), this.listPageFormName());

    if (element.args() && !isCalledFromLP)
    {
        element.args().copyCallerQuery(CopyCallerQuery::No);
    }

    //this form control includes PMF and PDS controls
    formCtrl = PdsCWFormCtrl_SalesQuotationTable::newFromForm(element);
    formCtrl.initPre();

    super();

    formCtrl.setDatasources(salesQuotationLine,salesQuotationTable);
    formCtrl.initPost();

    crmLicense = smmLicense::crm();

    quotationType = element.args().parmEnum();

    if (callerRecord.TableId == tableNum(smmBusRelTable))
    {
        busRelTable = callerRecord;
        if (busRelTable.BusRelTypeId == smmBusRelTypeGroup::getCustomerType())
        {
            callerRecord = CustTable::findByPartyRecId(busRelTable.Party);
        }
    }

    this.constructSalesQuotationTableForm(callerRecord);

    TaxVATNumTable::enableLookupVATNum(vatNum);

    element.updateDesign(InventDimFormDesignUpdate::Init);

    salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, QuotationType)).allowEdit(salesQuotationTableForm.editQuotationType());

    MCRChannelUser mcrChannelUser = MCRChannelUser::find();

    if (isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter)) && mcrChannelUser)
    {
        // Since it is taking the default channel so disabled the channel selection.
        salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, RetailChannelTable)).allowEdit(false);
    }

    salesQuotationTable_ds.query(salesQuotationTableForm.querySalesQuotationTable(salesQuotationTable_ds.query()));
    salesQuotationLine_ds.query(salesQuotationTableForm.querySalesQuotationLine(salesQuotationLine_ds.query()));

    if (! TaxParameters::find().CashDiscOnInvoice)
    {
        salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, CashDiscPercent)).allowEdit(false);
    }

    if (custParameters.AutomaticTotalDiscount)
    {
        salesQuotationTable_ds.object(fieldNum(SalesQuotationTable, DiscPercent)).allowEdit(false);
    }

    busRelTypeId.visible(crmLicense);
    // Set whether source Id should be mandatory
    enableDirectedSelling = RetailMCRChannelTable::mcrEnableDirectedSellingFindByUser();
    enableOrderPriceControl = #MCRPriceOverrideEnabled && RetailMCRChannelTable::mcrEnableOrderPriceControlFindByUser();
    MCRPriceHistory.visible(custParameters.MCREnablePriceDetails);
    enableOrderPriceControl_Workflow = RetailPricingSalesDocumentHelper::isMcrPriceControlEnabledForQuotation(SalesQuotationTable.QuotationId);

    mcrSalesQuotationTable_ds.object(fieldNum(MCRSalesQuotationTable, SourceId)).mandatory(mcrOrderParameters.mcrSourceCodeRequired);
    mcrSalesQuotationLine_ds.object(fieldNum(MCRSalesQuotationLine, SourceId)).mandatory(mcrOrderParameters.mcrSourceCodeRequired);
    mcrSalesQuotationTable_ds.object(fieldNum(MCRSalesQuotationTable, SourceId)).visible(enableDirectedSelling);
    mcrSalesQuotationTable_ds.object(fieldNum(MCRSalesQuotationTable, PriceOverride)).visible(enableOrderPriceControl);
    mcrSalesQuotationLine_ds.object(fieldNum(MCRSalesQuotationLine, SourceId)).visible(enableDirectedSelling);
    mcrSalesQuotationLineOverride_ds.object(fieldNum(MCRSalesQuotationLineOverride, PriceOverride)).visible(enableOrderPriceControl);
    mcrSalesQuotationTable_ds.object(fieldNum(MCRSalesQuotationTable, PriceOverride)).visible(enableOrderPriceControl);
    mcrSalesQuotationLineOverride_ds.object(fieldNum(MCRSalesQuotationLineOverride, ReasonCode)).visible(enableOrderPriceControl);
    mcrSalesQuotationLineOverride_ds.object(fieldNum(MCRSalesQuotationLineOverride, ListPrice)).visible(enableOrderPriceControl);
    mcrSalesQuotationLineOverride_ds.object(fieldNum(MCRSalesQuotationLineOverride, ListPriceCurrencyCode)).visible(enableOrderPriceControl);
    salesQuotationLine_MCRMarginIcon.visible(salesParameters.mcrEnableMarginAlert);
    mcrMarginPercentStr.visible(salesParameters.mcrEnableMarginAlert);
    marginMap = new Map(Types::String, Types::Container);

    bankLGFeatureChecker    = BankLGFeatureChecker::construct();
    enableLGFeature         = bankLGFeatureChecker.checkFeatureEnabled();
    bankLGRequestForm.visible(enableLGFeature);
    salesQuotationTable_DS.object(fieldNum(SalesQuotationTable, BankDocumentType)).visible(enableLGFeature);

    pcExecute.visible(enableConfigurationFeature);

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        GlobalizationInstrumentationHelper::featureRun(GlobalizationConstants::FeatureReferenceBR00057, funcName());
        salesPurchOperationTypeController_BR = new SalesPurchOperationTypeController_BR(this.name());
    }
    // </GBR>
    logisticsPostalAddressHeader_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, CountryRegionId));
    logisticsPostalAddressHeader_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, ValidFrom));
    logisticsPostalAddressHeader_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, ValidTo));
    logisticsPostalAddressLine_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, CountryRegionId));
    logisticsPostalAddressLine_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, ValidFrom));
    logisticsPostalAddressLine_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, ValidTo));

    // <GTE>
    if (TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
    {
        TaxDocumentLauncher.visible(true);
        TaxTransSource.visible(false);
        LineSalesTax.visible(false);
        LineSalesTax_TaxGroup.visible(false);
        LineSalesTax_TaxItemGroup.visible(false);
    }

    if (TaxIntegrationUtils::isTaxInformationEnabled())
    {
        TransTaxInformation.visible(true);
    }
    // </GTE>

    isKittingEnabled = FeatureStateProvider::IsFeatureEnabled(KittingFeature::instance());

    if (isTaxIntegrationEnabledForSales)
    {
        salesQuotationTableForm.controlTaxIntegrationFieldsForTableAndLine(SalesQuotationTable_ds, SalesQuotationLine_ds);
    }

    TradeFormsInstrumentationActivities::instrumentTradeFormQueryFilters(formStr(SalesQuotationTable), salesQuotationTable_ds.query());

    if (RetailPricingFixOverrideWorkflowNotificationFlight::instance().isEnabled() && this.args())
    {
        Args args = this.args();

        switch (args.menuItemName())
        {
            case menuItemDisplayStr(SalesQuotationTable_RetailPriceOverrideWorkflow):
                if (args.lookupTable() == tableNum(SalesQuotationLine))
                {
                    SalesQuotationLine salesQuotationLineLoc = SalesQuotationLine::findRecId(str2int64(args.lookupValue()));
                    args.lookupTable(tableNum(SalesQuotationTable));
                    args.lookupField(fieldNum(SalesQuotationTable, RecId));
                    args.lookupValue(int642str(salesQuotationLineLoc.salesQuotationTable().RecId));
                }
                break;
        }
    }
}

