    return _newQuotationId;
}

public LogisticsLocationRecId parmPostalAddressLocation(LogisticsLocationRecId _postalAddressLocation = 0)
{
    if (!_postalAddressLocation)
    {
        // Set location by active datasource
        if (element.dataSource().name() == salesQuotationLine_ds.name())
        {
            _postalAddressLocation = salesQuotationLine.deliveryLocation();
        }
        else
        {
            _postalAddressLocation = salesQuotationTable.deliveryLocation();
        }
    }

    postalAddressLocation = _postalAddressLocation;
    return postalAddressLocation;
}

void pcConfigureLine()
{
    salesQuotationLine_DS.write();

    if (PCRuntimeLibrary::isConstraintBasedConfigurable(salesQuotationLine.ItemId))
    {
        PCExecuteVariantConfiguration::execute(salesQuotationLine, salesQuotationLine.ItemId, inventDim.ConfigId);
    }

    salesQuotationLine_DS.research(true);
}

void recalculateRetailPricesDiscounts()
{
    eventSource.EventWritePricingFormAction(formStr(SalesQuotationTable), FullPriceCalculationActionStr, correlationId);

    RecId  callerRecordRecId = salesQuotationLine.RecId;
    RetailSalesQuotationTable retailQuotationTable = salesQuotationTable.retailSalesQuotationTable();
    RetailSalesQuotationCalculator::setPricesDiscountsOnOrderOnServer(salesQuotationTable.QuotationId, retailQuotationTable.TotalManualDiscountPercentage);

    retailOrderNeedRecalculation = false;

    salesQuotationLine_ds.research(true);
}

void refreshSalesQuotationLine_IN()
{
    salesQuotationLine_ds.reread();
    salesQuotationLine_ds.refreshEx();
    salesQuotationLineTaxExtensionIN_ds.reread();
    salesQuotationLineTaxExtensionIN_ds.refresh();
}

public void rereadDatasource()
{
    // Refresh the sales quotation data source from another form
    salesQuotationTable_ds.reread();
}

SalesQuotationTable salesQuotationTable()
