}

/// <summary>
///     Updates product dimensions on the sales quotation line.
/// </summary>
/// <param name="_inventDim">
///     An instance of the <c>_inventDim</c> that provides the new product dimensions.
/// </param>
internal void updateProductDimensions(InventDim _inventDim)
{
    Set productDimensionFields = inventDim.copyProductDimensionsAndGetModifiedDimensionFields(_inventDim, salesquotationLine.ItemId);
    if (productDimensionFields)
    {
        salesQuotationLine.InventDimId = InventDim::findOrCreate(inventDim).inventDimId;
        salesQuotationLine.initFromProductDimensions(_inventDim);

        element.modifyInventDimSet(inventDim, productDimensionFields);

        salesQuotationLine_DS.cacheCalculateMethod(tableMethodStr(SalesQuotationLine, itemName));
    }
}

