}

/// <summary>
/// Check if workflow action button will be displayed.
/// </summary>
/// <param name = "_workflowType">
/// Workflow type.
/// </param>
/// <returns>
/// Return true if any line needs to submit and not submitted.
/// </returns>
public boolean canSubmitToWorkflow(str _workflowType = '')
{
    boolean ret;
    MCRSalesQuotationLine mcrSalesQuotationLineLocal;
    SalesQuotationLine salesQuotationLineLocal;

    // if current mcr line is not created or not submitted or approved
    if (!mcrSalesQuotationLine || (mcrSalesQuotationLine.RetailPriceOverrideWorkflowState == RetailPriceOverrideWorkflowState::NotSubmitted || mcrSalesQuotationLine.RetailPriceOverrideWorkflowState == RetailPriceOverrideWorkflowState::Approved || mcrSalesQuotationLine.RetailPriceOverrideWorkflowState == RetailPriceOverrideWorkflowState::None))
    {
        // and if any non submit line is need to submit for approval
        select firstonly RecId from mcrSalesQuotationLineLocal
            join RecId from salesQuotationLineLocal
                where mcrSalesQuotationLineLocal.SalesQuotationLine == salesQuotationLineLocal.RecId && salesQuotationLineLocal.QuotationId == salesQuotationTable.QuotationId
                   && mcrSalesQuotationLineLocal.RetailPriceOverrideWorkflowState == RetailPriceOverrideWorkflowState::NotSubmitted;

        if (mcrSalesQuotationLineLocal)
        {
            ret = true;
        }
    }

    // hide workflow actioin button even if the line is rejected and the line's status is None and no not submitted lines
    element.design().controlName("WorkflowActionBarButtonGroup").visible(ret || mcrSalesQuotationLine.RetailPriceOverrideWorkflowState != RetailPriceOverrideWorkflowState::None);

    return ret;
}

/// <summary>
/// Determines whether the <c>MCRInventSearch</c> logic is enabled.
/// </summary>
/// <returns>true if the <c>MCRInventSearch</c> logic is enabled; otherwise, false.</returns>
protected boolean isMCRInventSearchEnabled()
{
    return isConfigurationkeyEnabled(configurationKeyNum(TradeItemSearch)) && !productFromLookup
        && MCRFullTextParameters::isProductSearchDialogEnabled();
}

/// <summary>
/// Customizes the options used to populate the Office Menu.
/// </summary>
/// <param name = "_menuOptions">The menu options to be customized.</param>
public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    ListIterator dataEntityOptionsIterator = new ListIterator(_menuOptions.dataEntityOptions());

    while (dataEntityOptionsIterator.more())
    {
        OfficeMenuDataEntityOptions dataEntityOptions = dataEntityOptionsIterator.value();

        if (dataEntityOptions.dataEntityName() == tableStr(ProjQuotationHeaderEntity) || dataEntityOptions.dataEntityName() == tableStr(ProjQuotationLineEntity))
        {
            dataEntityOptionsIterator.delete();
        }
        else
        {
            dataEntityOptionsIterator.next();
        }
    }
}

