    {
        RetailSalesQuotationTable retailSalesQuotation = salesQuotationTable.retailSalesQuotationTable();
        return retailSalesQuotation.IsPriceAndDiscountRecalculationRequired;
    }
    else
    {
        return false;
    }
}

