}

/// <summary>
/// Prompts the user for a reason code when overriding the discount percent field.
/// </summary>
/// <returns>
/// true if the discount percent can be changed; otherwise, false.
/// </returns>
public boolean mcrGetOverrideResponse()
{
    boolean                               currentUserPermission;
    Args                                  args;
    container                             userIdReasonCode;
    RetailInformationSubcodeId            retailInformationSubcodeId;
    boolean                               cancelOverride;

    // only check override parameters if the system parameter is set to false
    if (#MCRPriceOverrideEnabled && enableOrderPriceControl)
    {
        // Do not allow the user to manipulate the form while a dialog is open.
        element.mcrDisableForm();

        MCROverrideLogin mcrOverrideLogin = new MCROverrideLogin();
        retailInformationSubcodeId = mcrOverrideLogin.launchPriceOverrideReasonCode(salesQuotationTable, NoYes::Yes);
        if (retailInformationSubcodeId)
        {
            element.mcrHeaderPriceOverride( false,
                                            retailInformationSubcodeId,
                                            curUserId());
        }
        else
        {
            cancelOverride = true;
        }

        element.mcrEnableForm();
    }

    return !cancelOverride;
}

