int  active()
{
    int                         ret;
    SalesQuotationTableType     editSalesQuotationTableType = salesQuotationTable.type();

    lastHeaderSourceId = mcrSalesQuotationTable.SourceId;

    ret = super();

    element.enableControlsOnSalesQuotationActive();

    return ret;
}

void  create(boolean  append = true)
{
    SalesQuotationTable     newSalesQuotationTable;

    element.editQuotation(true);

    salesQuotationTableForm.salesQuotationTable(salesQuotationTable);

    if (salesQuotationTableForm.create())
    {
        newSalesQuotationTable = salesQuotationTableForm.salesQuotationTable();

        if (newSalesQuotationTable)
        {
            super(append);

            if (isCalledFromWorkSpace)
            {
                QueryBuildDataSource qbds = salesQuotationTable_ds.query().dataSourceName(salesQuotationTable_ds.name());
                qbds.clearDynalinks();
                qbds.clearRanges();

                salesQuotationTable_ds.executeQuery();
            }

            salesQuotationTable.data(newSalesQuotationTable);
            salesQuotationTable_ds.rereadReferenceDataSources();

            lastHeaderSourceId = mcRsalesQuotationTable.SourceId;
            lineViewLines.setFocus();
            // Context is now editing. If parameter is ForNew, change it to ForEdit
            if (element.args().parm() == identifierStr(FormOpenModeForNew))
            {
                element.args().parm(identifierStr(FormOpenModeForEdit));
            }

            salesQuotationLine.clear();
            logisticsPostalAddressLine.clear();
        }
    }
    else
    {
        if (element.args().parm() == identifierStr(FormOpenModeForNew) && TradeFormHelper::isCalledFromForm(element.args()))
        {
            element.close();
        }
        else
        {
            this.editQuotation();
        }
    }
}

void  delete()
{
    if (salesQuotationTable.checkDelete())
    {
        if (salesQuotationTableType)
        {
            salesQuotationTableType.formMethodDataSourceDelete(element, salesQuotationTable_ds);
        }

        super();
    }
}

void editHeader()
{
    this.editQuotation();
}

void  editQuotation()
{
    element.editQuotation(element.editHeaderAllowed());
}

public void executeQuery()
{
    QueryBuildDataSource qbds = this.query().dataSourceName(salesQuotationTable_ds.name());

    if (convertedCustAccount)
    {
        qbds.clearDynalinks();
        qbds.clearRange(fieldNum(SalesQuotationTable,CustAccount));
        qbds.addRange(fieldNum(SalesQuotationTable,CustAccount)).value(convertedCustAccount);
        convertedCustAccount = '';
    }

    this.initRanges();

    typeRange.value(enum2str(QuotationType::Sales));

    super();
}

public void init()
{
    super();

    if (TradeFormHelper::isCalledFromForm(element.args(), formStr(CustTable))
        && element.args().record().TableId == tableNum(CustTable))
    {
        this.query().dataSourceTable(tableNum(SalesQuotationTable)).clearDynalinks();
    }

    // Enable caching of the document handling and note it display fields
    salesQuotationTable_ds.cacheAddMethod(tableMethodStr(SalesQuotationTable, showDocHandIcon));

    this.initRanges();

    logisticsPostalAddressHeader_ds.validTimeStateUpdate(ValidTimeStateUpdate::Correction);
    logisticsPostalAddressHeader_ds.query().validTimeStateDateTimeRange(DateTimeUtil::minValue(),
                                                                      DateTimeUtil::maxValue());
}

/// <summary>
/// Initializes ranges on SalesQuotationTable
/// </summary>
void initRanges()
{
    QueryBuildDataSource queryBuildDataSource = salesQuotationTable_ds.query().dataSourceTable(tableNum(SalesQuotationTable));

    typeRange = SysQuery::findOrCreateRange(queryBuildDataSource,fieldNum(SalesQuotationTable, QuotationType));
}

void linkActive()
{
    smmBusRelTable      smmBusRelTable;
    CustTable           custTable;
    DirPartyRecId       party;
    QuotationIdBase     quotationId;

    switch (element.args().dataset())
    {
        // Is the form called from the activities form
        case tableNum(smmActivities) :
            smmActivityParentLink::updateQuery(this.query().dataSourceTable(tableNum(SalesQuotationTable)), element.args().record());
            break;

        // Is form called from call list target
        case tableNum(smmTMCallListTable) :
            party = element.args().record().(fieldNum(smmTMCallListTable,Party));
            break;

        // Is form called from campaign target
        case tableNum(smmCampaignSelection) :
            party = element.args().record().(fieldNum(smmCampaignSelection,Party));
            break;

        // Is form called from smmBusRelTable
        case tableNum(smmBusRelTable) :
            party = element.args().record().(fieldNum(smmBusRelTable,Party));
            break;
    }

    if (party)
    {
        this.query().dataSourceTable(tableNum(SalesQuotationTable)).clearDynalinks();
        custTable = CustTable::findByPartyRecId(party);
        if (custTable)
        {
            this.query().dataSourceTable(tableNum(SalesQuotationTable)).addDynalink(fieldNum(SalesQuotationTable,CustAccount),custTable,fieldNum(CustTable,AccountNum));
        }
        else
        {
            smmBusRelTable = smmBusRelTable::findByParty(party);
            this.query().dataSourceTable(tableNum(SalesQuotationTable)).addDynalink(fieldNum(SalesQuotationTable,BusRelAccount),smmBusRelTable,fieldNum(smmBusRelTable,BusRelAccount));
        }
    }

    if (salesQuotationTableForm.linkActive())
    {
        salesQuotationTableForm.salesQuotationTable(element.args().record());
        salesQuotationTable_ds.query(salesQuotationTableForm.querySalesQuotationTable(salesQuotationTable_ds.query()));
    }

    if (salesQuotationTableType)
    {
        salesQuotationTableType.formMethodDataSourceLinkActive(element, salesQuotationTable_ds);
    }

    super();

    quotationId = salesQuotationTableForm.getQuotationIdFromCaller(element.args().caller());
    if (quotationId && quotationId != salesQuotationTable.QuotationId)
    {
        salesQuotationTable_ds.positionToRecordByValue(fieldNum(SalesQuotationTable, QuotationId), quotationId);
    }
}

void refreshDDC()
{
    if (salesQuotationTable_ds && salesQuotationTable && salesQuotationTable.DeliveryDateControlType)
        salesQuotationTable_ds.refresh();
}

