public boolean validate()
{
    CFOPAttributesDeterminer_BR cfopAttributesDeterminer;
    boolean ret;

    cfopAttributesDeterminer    = CFOPAttributesDeterminer_BR::construct(salesQuotationLine);
    ret                         = salesPurchOperationTypeController_BR.validate(salesQuotationTable.SalesPurchOperationType_BR, cfopAttributesDeterminer);

    if (ret && !salesQuotationTable.createInventTrans_BR())
    {
        if (Box::yesNo("@GLS104", DialogButton::No) == DialogButton::No)
        {
            return false;
        }
    }

    return super() && ret;
}

