public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    CustTable custTable;
    smmBusRelTable smmBusRel;
    container inUseAddresses = LogisticsLocationSelectionLookup::transactionAssociatedAddresses(
        tableNum(SalesQuotationLine), fieldNum(SalesQuotationLine, DeliveryPostalAddress),
        fieldNum(SalesQuotationLine, QuotationId), salesQuotationTable.QuotationId,
        salesQuotationTable.DeliveryPostalAddress);

    if (salesQuotationTable.CustAccount)
    {
        custTable = CustTable::find(salesQuotationTable.CustAccount);
        return LogisticsLocationSelectionLookup::lookupAddressForm(_formReferenceControl,
            custTable,
            [LogisticsLocationRoleType::Delivery],
            false,
            inUseAddresses,
            SalesQuotationLine_DS.cursor());
    }
    else
    {
        smmBusRel = smmBusRelTable::find(salesQuotationTable.BusRelAccount);
        return LogisticsLocationSelectionLookup::lookupAddressForm(_formReferenceControl,
            smmBusRel,
            [LogisticsLocationRoleType::Delivery],
            false,
            inUseAddresses,
            SalesQuotationLine_DS.cursor());
    }
}

