public void lookup(FormControl _formControl, str _filterStr)
{
    Args    args;
    FormRun formRun;

    if (EcoResProductParameters::find().pcLookupMethod == PCLookupMethod::Configurable)
    {
        args = new Args(formStr(PCProductLookup));
        args.caller(element);
        formRun = classfactory.formRunClass(args);
        formRun.init();
        this.performFormLookup(formRun, _formControl);
    }
    else if (isConfigurationkeyEnabled(configurationKeyNum(TradeItemSearch)))
    {
        if (isProductSearchLookupEnabled)
        {
            EcoResProductSearchLookup::lookupProduct(_formControl);
        }
        else
        {
            InventTable::lookupItemsForQuotes(_formControl, mcrSalesQuotationLine.SourceId);
        }
    }
    else
    {
        super(_formControl, _filterStr);
    }
}

void  modified()
{
    // <GIN>
    InventTable     inventTableLoc;
    #isoCountryRegionCodes
    // </GIN>
    super();

    salesQuotationLine_ds.resetSalesQuotationLineOnSalesCategoryModified();

    if (productFromLookup) // if there was product selected from the product lookup
    {
        SalesQuotationLineType salesQuotationLineType = salesQuotationLine.type();
        salesQuotationLineType.setSalesUnitFromItem();

        element.updateProductDimensions(InventDim::find(productFromLookup.InventDimId));
        productFromLookup = null;
    }

    salesQuotationLine.itemIdChanged();

    if (kittingFormHandler)
    {
        kittingFormHandler.initItem(salesQuotationLine);
    }

    salesQuotationLine_ds.changedInventoriedStatus();
    salesQuotationLine_ds.cacheCalculateMethod(tableMethodStr(SalesQuotationLine, itemName));

    // Reread ReferenceGroup controls, e.g. to show the category name.
    salesQuotationLine_ds.rereadReferenceDataSources();

    formCtrl.fieldModified(fieldNum(SalesQuotationLine,ItemId));

    element.enableLinePCActions();

    if (isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter)))
    {
        salesQuotationLine_ds.mcrFormInitSourceCode();
        element.mcrSetLineControls();
    }

    // set original price when item is entered or changed
    if (isRetailOrder && enableOrderPriceControl_Workflow)
    {
        retailSalesQuotationLine.OriginalPrice = salesQuotationLine.SalesPrice;
    }

    if (kittingFormHandler && kittingFormHandler.isKit())
    {
        kittingFormHandler.setLineAccess(salesQuotationLine_ds, InventDim_DS);
    }
}

