public boolean validate()
{
    boolean ret;
    DiscAmount originalLineDisc;

    ret = super();

    if (ret)
    {
        originalLineDisc = salesQuotationLine.lineDisc;
        salesQuotationLine.lineDisc = salesQuotationLine.salesPurchLineInterface().getRetailLineDisc();

        ret = salesQuotationLine_ds.mcrGetOverrideResponse(fieldNum(SalesQuotationLine, SalesPrice),
                                                           salesQuotationLine.SalesPrice,
                                                           fieldNum(MCRPriceOverrideMarkUpGroup, CostMarkUpPercent));

        salesQuotationLine.lineDisc = originalLineDisc;
    }

    return ret;
}

public void modified()
{
    super();

    this.setPricesAndDiscounts();

    element.mcrRefreshMarginAlert();

    if (isRetailOrder && enableOrderPriceControl_Workflow)
    {
        salesQuotationLine_ds.mcrSetWorkflowApprovalStatus();

        if (!retailSalesQuotationLine.OriginalPrice)
        {
            if (salesQuotationLine.orig().SalesPrice)
            {
                retailSalesQuotationLine.OriginalPrice = salesQuotationLine.orig().SalesPrice;
            }
            else
            {
                retailSalesQuotationLine.OriginalPrice = salesQuotationLine.SalesPrice;
            }
        }

        if (retailSalesQuotationLine.OriginalPrice != salesQuotationLine.SalesPrice)
        {
            retailSalesQuotationLine.IsPriceOverridden = NoYes::Yes;
        }
        else
        {
            retailSalesQuotationLine.IsPriceOverridden = NoYes::No;
        }
    }
}

[Replaceable]
protected void setPricesAndDiscounts()
{
    if (RetailPricingEngine::useRetailPricingEngineForSalesQuatation(salesQuotationTable.QuotationId))
    {
        salesQuotationLine.setPriceDisc(inventDim);

    }
}

void  modified()
{
    boolean ok = true;
    // <GIN>
    #ISOCountryRegionCodes
    // </GIN>

    if (kittingFormHandler && kittingFormHandler.isKit() && !salesQuotationLine.ManualEntryChangepolicy)
    {
        SalesQuotationLine.setPriceDisc(SalesQuotationLine.inventDim());
    }

    super();

    element.checkCreditNoteLine();

    if (SalesQuotationLineEstimatedMarginFlight::instance().isEnabled())
    {
        element.mcrRefreshMarginAlert();
    }

    salesQuotationLine_ds.refresh();
    salesQuotationLine_ds.refreshDDC();
}

void  modified()
{
    super();

    salesQuotationLine_ds.refresh();
}

public int active()
{
    int     ret;
    boolean allowEditLine;

    ret = super();

    if (salesQuotationLine.isCategoryBased())
    {
        allowEditLine = salesQuotationTableForm.editLineAllowed();
        salesQuotationLineForeignTradeCategory_ds.allowEdit(allowEditLine);
    }
    else
    {
        salesQuotationLineForeignTradeCategory_ds.allowEdit(false);
    }

    return ret;
}

public boolean validateWrite()
{
    boolean ret = true;

    if (salesQuotationLine.RecId && salesQuotationLine.isCategoryBased())
    {
        ret = super();
    }

    return ret;
}

