internal void modifyCustomInventDimension(FieldId _fieldId)
{
    if (InventDim::isFieldIdProductDimension(_fieldId))
    {
        element.modifyInventDimField(_fieldId);
        salesQuotationLine_ds.cacheCalculateMethod(tableMethodStr(SalesQuotationLine, itemName));

        if (isKittingEnabled)
        {
            element.kittingInventDimFieldModified(inventDim_ds, _fieldId);
        }
    }
    else if (InventDim::isFieldIdTrackingDimension(_fieldId))
    {
        salesQuotationLine.modifyInventDim(inventDim, _fieldId);
    }

    element.modifyInventDimFieldSalesCalcAvailableDlvDates();

    element.mcrRefreshMarginAlert();
}

