public void modified()
{
    MCRDefaultDimensions    mcrDefaultDimensionsLocal;
    MCRSourceCode           mcrSourceCodeLocal;
    CustTable               custTableLocal;

    super();

    if (lastHeaderSourceId != mcrSalesQuotationTable.SourceId)
    {
        mcrSourceCodeLocal = MCRSourceCode::construct(salesQuotationTable);
        if (mcrSalesQuotationTable.SourceId)
        {
            // Can assume source code is valid after call to MCRInitFromSourceCodeSetup.
            mcrSourceCodeLocal.mcrInitFromSourceCodeSetup(MCRSourceCodeSetup::find(mcrSalesQuotationTable.SourceId),
                                                                            salesQuotationTable.CustAccount);
        }
        else
        {
            custTableLocal                      = CustTable::find(salesQuotationTable.CustAccount);
            salesQuotationTable.MultiLineDisc   = custTableLocal.MultiLineDisc;
            salesQuotationTable.EndDisc         = custTableLocal.EndDisc;

            mcrDefaultDimensionsLocal = MCRDefaultDimensions::construct(salesQuotationTable);
            mcrDefaultDimensionsLocal.setDefaultDimensions();
        }

        // Update total discount now, or a user-entered change will be trapped
        // and considered an override, possibly of an old value, which is not
        // desirable.
        salesQuotationTable.updateFinalDisc();

        lastHeaderSourceId = mcrSalesQuotationTable.SourceId;

        salesQuotationTable_ds.refresh();
    }
}

public void lookup(FormControl _formControl, str _filterStr)
{
    MCRSourceCodeSetup::lookupNonExpiredSourceCodes(_formControl);
}

public void create(boolean _append = false)
{
    super();

    //If the source code is set on the header, default it to the sales line.
    MCRSalesQuotationTable mcrSalesQuotationTableLocal =  salesQuotationTable.mcrSalesQuotationTable();
    if (mcrSalesQuotationTableLocal.SourceId)
    {
        mcrSalesQuotationLine.SourceId = mcrSalesQuotationTableLocal.SourceId;
        MCRSourceCodeSetup souceCodeSetup = MCRSourceCodeSetup::find(mcrSalesQuotationLine.SourceId);
        retailSalesQuotationLine.Catalog = souceCodeSetup.Catalog;
    }
}

display container mcrWorkflowIcon(MCRSalesQuotationLine _mcrSalesQuotationLine)
{
    SymbolFontGlyphName symbol;
    if (_mcrSalesQuotationLine.RetailPriceOverrideWorkflowState != RetailPriceOverrideWorkflowState::None)
    {
        if (_mcrSalesQuotationLine.RetailPriceOverrideWorkflowState == RetailPriceOverrideWorkflowState::Approved)
        {
            symbol = ImageReferenceSymbol::FieldCorrect;
        }
        else if (_mcrSalesQuotationLine.RetailPriceOverrideWorkflowState == RetailPriceOverrideWorkflowState::Rejected)
        {
            symbol = ImageReferenceSymbol::Error;
        }
        else if (_mcrSalesQuotationLine.RetailPriceOverrideWorkflowState == RetailPriceOverrideWorkflowState::Submitted)
        {
            symbol = ImageReferenceSymbol::Workflow;
        }
        else
        {
            symbol = ImageReferenceSymbol::Warning;
        }
    }
    return ImageReference::constructForSymbol(symbol).pack();
}

public void lookup(FormControl _formControl, str _filterStr)
{
    MCRSourceCodeSetup::lookupNonExpiredSourceCodes(_formControl);
}

