public boolean validate()
{
    boolean ret = super();

    if (ret && enableOrderPriceControl)
    {
        ret = salesQuotationLine_ds.mcrGetOverrideResponse(fieldNum(RetailSalesQuotationLine, LineManualDiscountPercentage),
                                                        retailSalesQuotationLine.LineManualDiscountPercentage,
                                                        fieldNum(MCRPriceOverrideMarkUpGroup, DiscPercentOverride));
    }

    return ret;
}

public void clicked()
{
    if (element.shouldRecalculate())
    {
        eventSource.EventWritePricingFormAction(formStr(SalesQuotationTable), TotalsButtonClickedActionStr, correlationId);
        if (element.isRetailPriceAndDiscountRecalculationRequired())
        {
            element.recalculateRetailPricesDiscounts();
        }
    }

    if (CustParameters::find().AutomaticTotalDiscount)
    {
        element.calculateTotalDiscount();
    }

    super();

    if (isTaxIntegrationEnabledForSales)
    {
        salesQuotationTableForm.rereadTableAndLines(SalesQuotationTable_ds, SalesQuotationLine_ds);
    }
}

void clicked()
{
    SalesQuotationTotals::showTax(menuitemDisplayStr(TaxTmpWorkSalesQuote), salesQuotationTable);

    if (isTaxIntegrationEnabledForSales)
    {
        salesQuotationTableForm.rereadTableAndLines(SalesQuotationTable_ds, SalesQuotationLine_ds);
    }
}

