}

public str toolTip()
{
    DictEnum    dictEnum = new DictEnum(enumNum(LineDeliveryType));

    return dictEnum.label() + ': ' + dictEnum.value2Name(salesQuotationLine.LineDeliveryType);
}

public boolean modified()
{
    boolean ret;
    boolean recordsCreated;
    EcoResProductRecId productRecId;

    if (element.isMCRInventSearchEnabled())
    {
        [productRecId, recordsCreated] = MCRInventSearch::searchProduct(salesQuotationLine, salesQuotationLine_ItemId.text());

        // DataSource ItemId.modified() is called on the MCRSalesQuickQuoteForm, if a product is returned then
        //  it means the line was not updated there, so set ItemId and update product dimensions based on returned product.
        if (productRecId)
        {
            InventDistinctProduct distinctProduct = InventDistinctProduct::findByProduct(productRecId);
            ItemId newItemId = distinctProduct ? distinctProduct.ItemId : InventTable::findByProduct(productRecId).ItemId;

            // If the user entered an item ID, skip refresh
            if (newItemId != salesQuotationLine_ItemId.text())
            {
                salesQuotationLine.ItemId = newItemId;
                salesQuotationLine_ds.refresh();
            }

            element.setProductFromLookup(distinctProduct);

            ret = super();
        }
        else
        {
            ret = recordsCreated;
        }
    }
    else
    {
        ret = super();
    }
    return ret;
}

