public void jumpRef()
{
    if (ContactJumpRefGridFormViewOpensIfContactNotSetFlight::instance().isEnabled()
        && !salesQuotationTable.ContactPersonId)
    {
        Args args = new Args();
        args.caller(element);
        args.formViewOption(FormViewOption::Grid);
        new MenuFunction(menuitemDisplayStr(ContactPerson), MenuItemType::Display).run(args);
    }
    else
    {
        quotation_ContactPersonId.jumpRef();
    }
}

