{
    ContactPerson::lookupDirContactPerson( quotation_contactPersonId,
                                           salesQuotationTable.party(),
                                           editContactPersonName2,
                                           salesQuotationTable.ContactPersonId);
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (!salesQuotationTable.ContactPersonId)
    {
        if (!ContactPerson::findNameParty(salesQuotationTable.party(), editContactPersonName2.text()))
        {
            salesQuotationTable.ContactPersonId = ContactPerson::findOrCreateNameParty(salesQuotationTable.party(), editContactPersonName2.text()).ContactPersonId;
        }
    }

    return ret;
}

