private void buildData(boolean _updateControls)
{
    inventReleaseOrderPickingTmp.SkipProductionOrderExistCalculation = skipProductionOrderExistsCalculation.value();
    inventReleaseOrderPickingTmp.SkipTransferOrderExistCalculation = skipTransferOrderExistsCalculation.value();
    InventReleaseOrderPickingForm_Sales::buildData(inventReleaseOrderPickingTmp, selectSalesLines.pack(), deductUnreserved.value());
    inventReleaseOrderPickingTmp_ds.research();

    latestUpdateDate = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());
    latestUpdateTime = DateTimeUtil::getTimeNow(DateTimeUtil::getUserPreferredTimeZone());

    if (_updateControls)
    {
        latestUpdateDateControl.update();
        latestUpdateTimeControl.update();
    }
}

