public boolean unpack(container _packedClass)
{
    Integer version = conPeek(_packedClass,1);

    switch (version)
    {
        case #CurrentVersion:
            [version,#CurrentList] = _packedClass;
            deductUnreserved.value(deductUnreservedPack);
            skipProductionOrderExistsCalculation.value(skipProductionOrderExistsCalculationPack);
            skipTransferOrderExistsCalculation.value(skipTransferOrderExistsCalculationPack);
            break;

        case #Version6:
            [version,#Version6List] = _packedClass;
            deductUnreserved.value(deductUnreservedPack);
            skipProductionOrderExistsCalculation.value(false);
            skipTransferOrderExistsCalculation.value(false);
            break;

        case 5:
            [version,deductUnreservedPack] = _packedClass;
            deductUnreserved.value(deductUnreservedPack);
            break;
        default:
            return false;
    }

    return true;
}

public void updateDesign(InventDimFormDesignUpdate _mode)
{
    InventDimParm dimParmVisible;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
        case InventDimFormDesignUpdate::LinkActive:
            element.inventDimSetupObject().initFromCaller();
            element.inventDimSetupObject().parmCtrlItemId(inventSum_ItemId);
            if (_mode == InventDimFormDesignUpdate::Init)
            {
                dimParmVisible = element.inventDimSetupObject().parmDimParmVisibleGrid();
                if (dimParmVisible.ItemIdFlag == NoYes::No)
                {
                    itemName.visible(false);
                }
                dimParmVisible.ClosedQtyFlag = NoYes::Yes; // Only include those with open quantities
                element.inventDimSetupObject().parmDimParmVisibleGrid(dimParmVisible);
            }
        // No break
        case InventDimFormDesignUpdate::Active:
            element.inventDimSetupObject().formActiveSetup();
            element.inventDimSetupObject().formSetControls(true,true);
            break;

        default:
            throw error(strFmt("@SYS54195",funcName()));
    }

    SalesQtyGroup.heightMode(0);
}

