public boolean validateWrite()
{
    boolean ret;
    boolean isCWItem;
    InventQty activateRemainder;
    PdsCWInventQty cwActivateRemainder;
    InventMovement movement;
    InventQty reservQty;
    InventQty allReadyReservedQty;
    PdsCWInventQty pdsCWReservQty;
    PdsCWInventQty pdsCWAllReadyReservedQty;
    PdsCatchWeightMovementQtyAdjustment pdsCatchWeightMovementQtyAdjustment;

    ret = super();

    if (ret && formCtrl)
    {
        ret = formCtrl.dataSourceValidateWrite();
    }

    isCWItem = PdsGlobal::pdsIsCWItem(salesLine.ItemId);

    if (!isCWItem)
    {
        activateRemainder = (salesLine.onOrder() + salesLine.reservedPhysical() + salesLine.reservedOnOrder()) - (salesLine.reservedOnPick() + salesLine.qtyOnPickNotReserved());

        if (salesLine.InventDeliverNow > activateRemainder)
        {
            return checkFailed("@SYS90343");
        }
    }
    else
    {
        cwActivateRemainder = (salesLine.pdsCWOnOrder() + salesLine.pdsCWReservedPhysical() + salesLine.pdsCWReservedOnOrder()) - (salesLine.pdsCWReservedOnPick() + salesLine.pdsCWQtyOnPickNotReserved());

        if (salesLine.PdsCWInventDeliverNow > cwActivateRemainder)
        {
            return checkFailed("@SYS4110510");
        }
    }

    // check for over activating
    if (ret)
    {
        ret = InventReleaseOrderPickingForm_Sales::checkOverActivate(salesLine, salesLine.InventDeliverNow, deductUnreserved.value(), salesLine.PdsCWInventDeliverNow);
    }

    if (ret)
    {
        movement = InventMovement::construct(salesLine);
        allReadyReservedQty = movement.transIdSum().reserved();
        pdsCWAllReadyReservedQty = movement.transIdSum().pdsCWReserved();

        //if order is using autoreservation the entire qty will be reserved when updating the line - if user is trying to reserve less than full qty message will be shown
        if (movement.mustBeAutoReserved() && (salesLine.InventDeliverNow != movement.transQty() && allReadyReservedQty != movement.transQty()))
        {
            if (isCWItem)
            {
                pdsCatchWeightMovementQtyAdjustment = PdsCatchWeightMovementQtyAdjustment::newBuffer(salesLine);
                salesLine.InventDeliverNow = pdsCatchWeightMovementQtyAdjustment.salesIssueQty(
                    DocumentStatus::PickingList,
                    salesLine.PdsCWInventDeliverNow,
                    salesLine.inventTable().inventUnitId());
                    salesLine.SalesDeliverNow = EcoResProductUnitConverter::convertGivenUnitSymbolsForReleasedProduct(salesLine.ItemId,
                                                                                                                      salesLine.InventDimId,
                                                                                                                      salesLine.InventDeliverNow,
                                                                                                                      salesLine.inventTable().inventUnitId(),
                                                                                                                      salesLine.SalesUnit,
                                                                                                                      NoYes::Yes);
            }

            info("@SYS112524");
        }
        else if (salesLine.InventDeliverNow != salesLine.orig().InventDeliverNow) // if ok - reserve qty
        {
            reservQty = (-allReadyReservedQty) - salesLine.InventDeliverNow - salesLine.reservedOnPick();
            pdsCWReservQty = (-pdsCWAllReadyReservedQty) - salesLine.PdsCWInventDeliverNow - salesLine.pdsCWReservedOnPick();

            if (!isCWItem && reservQty < 0)   //Activate now is being increased - always continue
            {
                InventUpd_Reservation::updateReserveBuffer(salesLine, reservQty);
                inventSum_ds.research();
            }
            else if (isCWItem && pdsCWReservQty < 0)    //Activate now is being increased - always continue
            {
                // Recalculate reservQty, to ensure it has the same sign when adjusting up and down
                reservQty = PdsCatchWeight::minInventQty(salesLine.ItemId, pdsCWReservQty);
                InventUpd_Reservation::updateReserveBuffer(salesLine, reservQty, InventMovSubType::None, pdsCWReservQty);
                salesLine.InventDeliverNow = -movement.transIdSum(true).reserved();
                salesLine.SalesDeliverNow = EcoResProductUnitConverter::convertGivenUnitSymbolsForReleasedProduct(salesLine.ItemId,
                                                                                                                  salesLine.InventDimId,
                                                                                                                  salesLine.InventDeliverNow,
                                                                                                                  salesLine.inventTable().inventUnitId(),
                                                                                                                  salesLine.SalesUnit,
                                                                                                                  NoYes::Yes);

                inventSum_ds.research();
            }
        }
    }

    return ret;
}

