public edit InventQty reserveNow(boolean _set, SalesLine  _salesLine, InventQty  _reserveNow)
{
    InventQty reservQty;
    InventMovement movement;

    if (_set)
    {
        if (PdsGlobal::pdsIsCWItem(_salesLine.ItemId))
        {
            throw error(strFmt("@PRO1994", _salesLine.ItemId));
        }
        if (_reserveNow < 0)
        {
            error("@SYS4200");
        }
        else
        {
            movement = InventMovement::construct(_salesLine);
            reservQty = (- movement.transIdSum().reserved()) - _reserveNow;

            if (reservQty)
            {
                InventUpd_Reservation::updateReserveBuffer(_salesLine, reservQty);
                salesLine_ds.reread();
                inventSum_ds.research();
                salesLineForReserveNow = 0; // ensure that cached values gets updated

                // write down activate now on sales line (inventDeliverNow) if reservation is reduced
                if (salesLine.InventDeliverNow > _salesLine.reservedPhysical())
                {
                    salesLine.InventDeliverNow = _salesLine.reservedPhysical();
                    salesLine_ds.write();
                }
            }
        }
    }

    if (_salesLine.RecId != salesLineForReserveNow)
    {
        salesLineForReserveNow = _salesLine.RecId;
        this.updateReserveNow(_salesLine);
    }

    return salesLineReserveNow;
}

