/// <summary>
/// Returns the current quantity of material reserved for selected inventory dimension
/// </summary>
/// <param name="_set">Specifiy if changed</param>
/// <param name="_salesLine">Current Salesline record</param>
/// <param name="_cwReserveNow">Current CW quantity to be updated</param>
/// <returns>Reserved quantity</returns>
public edit PdsCWInventQty pdsCWReserveNow(boolean _set, SalesLine _salesLine, PdsCWInventQty _cwReserveNow)
{
    PdsCWInventQty cwQty;
    PdsCWInventQty cwReservQty;
    InventQty reservQty;
    InventMovement movement;

    if (_set)
    {
        if (_cwReserveNow < 0)
        {
            error("@SYS4200");
        }
        else
        {
            movement = InventMovement::construct(_salesLine);
            cwQty = movement.pdsCWRemainPhysical();
            if (cwQty > 0)
            {
                cwQty = 0;
            }

            cwReservQty = -movement.transIdSum().pdsCWReserved() -_cwReserveNow;

            if (cwReservQty)
            {
                reservQty = PdsCatchWeight::minInventQty(_salesLine.ItemId,cwReservQty);
                InventUpd_Reservation::updateReserveBuffer(_salesLine, reservQty,InventMovSubType::None,cwReservQty);
                salesLine_ds.reread();
                inventSum_ds.research();

                // write down activate now on sales line (inventDeliverNow) if reservation is reduced
                if (salesLine.PdsCWInventDeliverNow
                        > _salesLine.pdsCWReservedPhysical())
                {
                    salesLine.PdsCWInventDeliverNow =
                            _salesLine.pdsCWReservedPhysical();
                    salesLine.InventDeliverNow = PdsCatchWeight::inventQty(
                                                    salesLine.ItemId,
                                                    salesLine.PdsCWInventDeliverNow);
                    salesLine_ds.write();
                }

                this.updateReserveNow(_salesLine);
            }
        }
    }

    cwQty = _salesLine.pdsCWReservedPhysical() + _salesLine.pdsCWReservedOnOrder();

    return cwQty;
}

