private boolean isSelectionChanged(InventDim _inventDim)
{
    return inventSum.ItemId != itemIdOnHand || !InventDim::isInventDimEqual(inventDimOnHand, _inventDim);
}

private void updateCache(boolean _selectionChanged, InventDim _inventDim, str _funcName)
{
    if (_selectionChanged)
    {
        itemIdOnHand = inventSum.ItemId;
        inventDimOnHand = _inventDim;
        cachedDisplayMethods = new Set(Types::String);
    }
    cachedDisplayMethods.add(_funcName);
}

public display InventQty availableForActivation(InventDim _inventDim)
{
    InventSum inventSumLocal;
    InventDimParm inventDimParm;
    str currentFunction  = funcName();
    boolean selectionChanged = this.isSelectionChanged(_inventDim);

    if (selectionChanged || !cachedDisplayMethods.in(currentFunction))
    {
        inventDimParm.initFromInventDim(_inventDim);
        inventSumLocal = InventSum::findSumQty(inventSum.ItemId,_inventDim,inventDimParm);

        inventSumLocal.ItemId = inventSum.ItemId;

        availableForActivation = max(inventAvailabilityFormCache.availability(inventSumLocal,_inventDim).availPhysical()
                                     - this.wmsOrderNotReserved(_inventDim),0);

        this.updateCache(selectionChanged, _inventDim, currentFunction);
    }
    return availableForActivation;
}

public display InventQty wmsOrderNotReserved(InventDim _inventDim)
{
    InventDimParm inventDimParm;
    str currentFunction = funcName();
    boolean selectionChanged = this.isSelectionChanged(_inventDim);

    if (selectionChanged || !cachedDisplayMethods.in(currentFunction))
    {
        inventDimParm.initFromInventDim(_inventDim);
        wmsOrderNotReserved = -InventTrans::qtyTotalWMSOrderNotReserved(salesLine.ItemId, _inventDim, inventDimParm);

        this.updateCache(selectionChanged, _inventDim, currentFunction);
    }
    return wmsOrderNotReserved;
}

/// <summary>
/// Returns the current qty of material available for selected inventory dimension
/// </summary>
/// <param name="_inventDim">Current InventDim record</param>
/// <returns>current available inventory</returns>
public display PdsCWAvailableForActivation pdsCWAvailableForActivation(InventDim _inventDim)
{
    InventSum inventSumLocal;
    InventDimParm inventDimParm;
    str currentFunction = funcName();
    boolean selectionChanged = this.isSelectionChanged(_inventDim);

    if (selectionChanged || !cachedDisplayMethods.in(currentFunction))
    {
        inventDimParm.initFromInventDim(_inventDim);
        inventSumLocal = InventSum::findSumQty(inventSum.ItemId,_inventDim,inventDimParm);
        cwAvailableForActivation = inventSumLocal.pdsCWAvailPhysicalCalculated() - this.pdsCWPickOrWMSOrderNotRes(_inventDim);

        if (cwAvailableForActivation < 0)
        {
            cwAvailableForActivation = 0;
        }

        this.updateCache(selectionChanged, _inventDim, currentFunction);
    }

    return cwAvailableForActivation;
}

/// <summary>
/// Returns the current qty of material with either on picklist or WmsOrder that are not reserved
/// </summary>
/// <param name="_inventDim">Current InventDim record</param>
/// <returns>Current inventory not reserved</returns>
public display PdsCWPickOrWMSOrderNotReserved pdsCWPickOrWMSOrderNotRes(InventDim _inventDim)
{
    InventDimParm inventDimParm;
    str currentFunction = funcName();
    boolean selectionChanged = this.isSelectionChanged(_inventDim);

    if (selectionChanged || !cachedDisplayMethods.in(currentFunction))
    {
        inventDimParm.initFromInventDim(_inventDim);
        cwPickOrWMSorderNotReserved = -InventTrans::pdsCWQtyTotalWMSOrderNotReserved(salesLine.ItemId, _inventDim, inventDimParm);

        this.updateCache(selectionChanged, _inventDim, currentFunction);
    }

    return cwPickOrWMSorderNotReserved;
}

