public void clicked()
{
    SalesTable  lastSalesTable;
    SalesLine   localSalesLine;
    Array       markedRecords;
    int         rowIndex;

    //  To support multiSelect mark the lines with a value in inventDeliverNow
    markedRecords = new Array(Types::Integer);
    rowIndex = 1;

    for (localSalesLine = salesLine_ds.getFirst();localSalesLine;localSalesLine = salesLine_ds.getNext())
    {
        if (localSalesLine.InventDeliverNow)
        {
            if (lastSalesTable.SalesId != localSalesLine.SalesId)
            {
                lastSalesTable  =   localSalesLine.salesTable();
            }

            if (localSalesLine.type(lastSalesTable).mayPickingListBeUpdated())
            {
                markedRecords.value(markedRecords.lastIndex() + 1, rowIndex);
            }
        }
        rowIndex++;
    }

    inventReleaseOrderPickingTmp_ds.markRecords(markedRecords);

    super();

    //  Unmark after posting
    markedRecords = new Array(Types::Integer);
    inventReleaseOrderPickingTmp_ds.markRecords(markedRecords);

    element.reCalculate();
}

public void clicked()
{
    super();

    element.reCalculate();
}
