Method.Source:
[Form]
public class SalesSummaryParameters extends FormRun
{
    SalesSummaryParametersForm  salesSummaryParametersForm_Quotation;
    SalesSummaryParametersForm  salesSummaryParametersForm_Confirmation;
    SalesSummaryParametersForm  salesSummaryParametersForm_PickingList;
    SalesSummaryParametersForm  salesSummaryParametersForm_PackingSlip;
    SalesSummaryParametersForm  salesSummaryParametersForm_Invoice;
    SalesSummaryParametersForm  salesSummaryParametersForm_Invoice4Paym;
    #ISOCountryRegionCodes

}

void init()
{
    // <GEERU>
    boolean    countryRegion_RU = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]);
    // </GEERU>

    super();

    salesSummaryParametersForm_Quotation = SalesSummaryParametersForm::construct(DocumentStatus::Quotation, selectedQuotation, notSelectedQuotation);
    salesSummaryParametersForm_Quotation.fillViews();

    salesSummaryParametersForm_Confirmation = SalesSummaryParametersForm::construct(DocumentStatus::Confirmation, selectedConfirmation, notSelectedConfirmation);
    salesSummaryParametersForm_Confirmation.fillViews();

    salesSummaryParametersForm_PickingList = SalesSummaryParametersForm::construct(DocumentStatus::PickingList, selectedPickingList, notSelectedPickingList);
    salesSummaryParametersForm_PickingList.fillViews();

    salesSummaryParametersForm_PackingSlip = SalesSummaryParametersForm::construct(DocumentStatus::PackingSlip, selectedPackingSlip, notSelectedPackingSlip);
    salesSummaryParametersForm_PackingSlip.fillViews();

    salesSummaryParametersForm_Invoice = SalesSummaryParametersForm::construct(DocumentStatus::Invoice, selectedInvoice, notSelectedInvoice);
    salesSummaryParametersForm_Invoice.fillViews();
    // <GEERU>
    if (countryRegion_RU)
    {
        salesSummaryParametersForm_Invoice4Paym = SalesSummaryParametersForm::construct(DocumentStatus::Invoice4Paym_RU, selectedInvoice4Paym, notSelectedInvoice4Paym);
        salesSummaryParametersForm_Invoice4Paym.fillViews();
        SetupInvoice4Paym_RU.visible(true);
    }
    // </GEERU>
}
