    [Form]
    public class SalesTableReferences extends FormRun
    {
        Map                 interCompanyOriginalSalesCache;
        Map                 interCompanyPurchCache;

}

public void init()
{
    super();

    this.interCompanyClearCache();

    salestable_ds.linkActive();
}

/// <summary>
///    Clears the two intercompany maps
/// </summary>
public void interCompanyClearCache()
{
    interCompanyOriginalSalesCache = new Map(typeName2Type(extendedTypeStr(recId)), Types::Container);
    interCompanyPurchCache         = new Map(typeName2Type(extendedTypeStr(recId)), Types::Container);
}

/// <summary>
///    Gets the original sales order from the intercompany map based on the given <c>SalesTable</c>.
/// </summary>
/// <param name = "_salesTable">
///     The <c>SalesTable</c> based on which to fetch the intercompany original sales order.
/// </param>
/// <returns>
///     A container which contains the original sales order info.
/// </returns>
public container interCompanyOriginalSalesCache(SalesTable _salesTable)
{
    if (!interCompanyOriginalSalesCache.exists(_salesTable.RecId))
    {
        interCompanyOriginalSalesCache.insert(_salesTable.RecId, _salesTable.interCompanyOriginalSalesInfo());
    }
    return interCompanyOriginalSalesCache.lookup(_salesTable.RecId);
}

/// <summary>
///    Gets or sets the intercompany purchase order info from the intercompany map based on the given <c>PurchTable</c>.
/// </summary>
/// <param name = "_purchTable">
///     The <c>PurchTable</c> based on which to get or set the intercompany purchase order info in the map.
/// </param>
/// <returns>
///     A container which contains the intercompany purchase order info.
/// </returns>
public container interCompanyPurchCache(PurchTable _purchTable)
{
    if (!interCompanyPurchCache.exists(_purchTable.RecId))
    {
        interCompanyPurchCache.insert(_purchTable.RecId,_purchTable.interCompanySalesInfo());
    }
    return interCompanyPurchCache.lookup(_purchTable.RecId);
}

public int active()
{
    int ret;

    ret = super();

    salesTable_ds.object(fieldNum(SalesTable, ReturnItemNum)).allowEdit(salesTable.returnItem());

    IntercompanyTab.visible(salesTable.InterCompanyOrder);

    interCompanyPurchSalesReference_ds.executeQuery();
    salesTable_Reference_ds.executeQuery();

    return ret;
}

void  linkActive()
{
    QueryBuildDataSource    queryBuildDataSource;
    SalesTable              tmpSalesTable;

    tmpSalesTable   =   element.args().record();

    queryBuildDataSource = this.query().dataSourceTable(tableNum(SalesTable));
    queryBuildDataSource.clearRanges();

    queryBuildDataSource.addRange(fieldNum(SalesTable, SalesId)).value(tmpSalesTable.SalesId);

    super();
}

/// <summary>
///    Gets the <c>InterCompanySalesType</c> from the intercompany map based on the given <c>PurchTable</c>.
/// </summary>
/// <param name = "_purchTable">
///     The <c>PurchTable</c> based on which to fetch the <c>InterCompanySalesType</c>.
/// </param>
/// <returns>
///     The <c>InterCompanySalesType</c> for the given <c>PurchTable</c>.
/// </returns>
display InterCompanySalesType interCompanySalesType(PurchTable _purchTable)
{
    InterCompanySalesType   salesType;

    salesType = conPeek(element.interCompanyPurchCache(_purchTable), 1);

    return salesType;
}

