[Form]
public class SrmParameters extends FormRun
{
    #ISOCountryRegionCodes

    NumberSeqReference      numberSeqReferencePurch;
    NumberSeqReference      numberSeqReferenceVendMgnt;

    boolean                 runExecuteDirect;
    TmpIdRef                tmpIdRef;

    FieldId                 tmpFieldId;

    PurchMatchingPolicyOption         enterMatchingPolicy;
    PurchMatchingPolicyOverrideOption enterMatchingPolicyOverride;

    NumberSeqScope             scope;
    NumberSeqApplicationModule numberSeqModulePurch;
    NumberSeqApplicationModule numberSeqModuleVendMgmt;
