void fillView()
{
    InventDimMetaDataCache::fillParameterFormView(listViewDimension);
    element.setupAllowEdit(false);
}

    public void init()
    {
        this.numberSeqPreInit();

        super();

        PurchParameters::find();
        PurchRFQParameters::find();
        VendParameters::find();

        CatParameters::find();
        CatUserReviewSettings::find();

        this.numberSeqPostInit();

        InventDimSetupGrid::loadSetup();

        this.fillView();

        element.setupExtendedPriceMatchingOptions(vendParameters.UseExtendedPriceMatching);
        element.setupMiscChargeMatchingOptions(vendParameters.UseMiscChargeMatching);

        this.initSrmParameters();
        // <GEERU>
        sumByCtrl = SalesPurchSummarySumByCtrl::newSumByControl(summaryupdate_SummaryUpdateBy);

        // if security key on the table is restricted to view or below, disable
        // the unbound controls to avoid data manipulation
        if (! hasTableAccess(tableNum(PurchParameters), AccessType::Edit))
        {
            summaryupdate_SummaryUpdateBy.enabled(false);
        }

        sumByCtrl.remove(AccountOrder::Journal);
        sumByCtrl.build();
        // </GEERU>
        TAMVendRebateParameters::find();

        if (!RetailMCRChannelTable::mcrEnableOrderCompletionFindByUser())
        {
            directDelivery.visible(false);
        }

        // <PubSect>
        if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
        {
            purchRFQParameters_ds.object(fieldNum(PurchRFQParameters,LockRFQOnSend)).allowEdit(false);
        }
        // </PubSect>

        VendorInformationFormConfiguration::find();

        this.setAllowEditForBidType(!PurchRFQParameters::find().DefaultSolicitationType);

        PurchRFQSealedBidFlightingManager::initSealedBiddingSpecificControl(element);

		updates_DecimalPrecisionCheckQty.visible(true);

        TabPurchaseRequisition.visible(PurchReqLineUseDeliveryAddressForWorkersFeature::instance().isEnabled());

        TabUpdates.visible(!SalesPurchSyncDeliveryInfoFeature::instance().isEnabled());
        TabUpdatesV2.visible(SalesPurchSyncDeliveryInfoFeature::instance().isEnabled());
    }

public void initCreditInvoicing()
{
    boolean vendCreditInvoicing = true;

    if (!SysCountryRegionCode::isLegalEntityInCountryRegion([#isoES, #isoSE, #isoPL]))
    {
        vendCreditInvoicing = false;
    }
}

protected void initSrmParameters()
{
    VendRequestManagementParameters::find();
}

void listSelection()
{
    int idx;

    if (inventDimSetupGrid.RecId)
    {
        inventDimSetupGrid.write();
    }

    idx = listViewDimension.getNextItem(FormListNext::Selected);

    if (idx <0)
    {
        element.setupAllowEdit(false);
        tmpFieldId = 0;
        inventDimSetupGrid_ds.executeQuery();
        inventDimSetupGrid_ds.refresh();
        return;
    }

    element.setupAllowEdit(true);

    tmpFieldId = listViewDimension.getItem(idx).data();

    inventDimSetupGrid_ds.executeQuery();
    inventDimSetupGrid_ds.refresh();
}

void numberSeqPostInit()
{
    numberSequenceReference_ds.object(fieldNum(NumberSequenceReference, AllowSameAs)).visible(numberSeqModulePurch.sameAsActive() || numberSeqModuleVendMgmt.sameAsActive());
    referenceSameAsLabel.visible(numberSeqModulePurch.sameAsActive() || numberSeqModuleVendMgmt.sameAsActive());
}

void numberSeqPreInit()
{
    runExecuteDirect   = false;
    numberSequenceModules = [NumberSeqModule::Purch, NumberSeqModule::VendMgmt];
    numberSeqModulePurch = new NumberSeqModulePurchaseOrder();
    numberSeqModuleVendMgmt = new NumberSeqModuleVendMgmt();
    scope = NumberSeqScopeFactory::createDataAreaScope();
    NumberSeqApplicationModule::createReferencesMulti(numberSequenceModules, scope);
    tmpIdRef.setTmpData(NumberSequenceReference::configurationKeyTableMulti(numberSequenceModules));
}

public void run()
{
    super();

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoES]))
    {
        this.updatePurchCycle(PurchParameters::find().PurchaseCycle);
        purchCycle_PurchaseCycle.value(PurchParameters::find().PurchaseCycle);
    }

    element.enabledVendorCategorySelectionLevel();
}

void setupAllowEdit(boolean _allow)
{
    inventDimSetupGrid_ds.object(fieldNum(InventDimSetupGrid, ShowGridPurchLine)).allowEdit(_allow);
    inventDimSetupGrid_ds.object(fieldNum(InventDimSetupGrid, ShowGridPurchLine)).skip(!_allow);
}

public void setupExtendedPriceMatchingOptions(UseExtendedPriceMatching _useExtendedPriceMatching)
{
    if (_useExtendedPriceMatching == ExtendedPriceMatching::None)
    {
        vendParameters.UseExtendedPriceMatching = ExtendedPriceMatching::None;
    }
}

/// <summary>
/// Enables and disables miscellaneous charges matching fields.
/// </summary>
/// <param name="_useInvoiceMiscChargeMatching">
/// NoYes enumeration used to enable and disable misc. charges matching fields.
/// </param>
/// <remarks>
/// Misc. charges matching is only enabled when Markup and PurchInvoiceMatching configuration keys are both enabled.
/// </remarks>
public void setupMiscChargeMatchingOptions(UseInvoiceMiscChargeMatching _useInvoiceMiscChargeMatching)
{
    boolean isMarkupConfigurationEnabled;

    isMarkupConfigurationEnabled = isConfigurationkeyEnabled(configurationKeyNum(Markup));

    if (!isMarkupConfigurationEnabled)
    {
        _useInvoiceMiscChargeMatching = NoYes::No;
    }

    if (_useInvoiceMiscChargeMatching == NoYes::No)
    {
        vendParameters.UseMiscChargeMatching = NoYes::No;
    }
}

public void updatePurchCycle(boolean _enabled)
{
    cycle.enabled(_enabled);
    selection.enabled(_enabled);
}

public void enabledVendorCategorySelectionLevel()
{
    VendRequestManagementParameters_VendorCategorySelectionLevel.enabled(VendRequestManagementParameters.VendorCategorySelectionLevelEnabled);

    if (!VendRequestManagementParameters.VendorCategorySelectionLevelEnabled)
    {
        VendRequestManagementParameters.VendorCategorySelectionLevel = vendorCategoryDisabledSelectionLevel;
    }
}

internal void enabledDeliveryInfoSyncFromPurchaseOrderToSalesOrder(boolean _setDeliveryInfoSyncFromPurchaseOrderToSalesOrder = true)
{
    if (SalesPurchSyncDeliveryInfoFeature::instance().isEnabled())
    {
        purchParameters_ds.object(fieldNum(PurchParameters, DeliveryInfoSyncFromPurchaseOrderToSalesOrderStock)).enabled(PurchParameters.DeliveryInfoSyncOnUpdateStock);
        purchParameters_ds.object(fieldNum(PurchParameters, DeliveryInfoSyncFromPurchaseOrderToSalesOrderDirectDelivery)).enabled(PurchParameters.DeliveryInfoSyncOnUpdateDirectDelivery);

        if (_setDeliveryInfoSyncFromPurchaseOrderToSalesOrder)
        {
            if (!PurchParameters.DeliveryInfoSyncOnUpdateStock)
            {
                PurchParameters.DeliveryInfoSyncFromPurchaseOrderToSalesOrderStock = NoYes::No;
            }

            if (!PurchParameters.DeliveryInfoSyncOnUpdateDirectDelivery)
            {
                PurchParameters.DeliveryInfoSyncFromPurchaseOrderToSalesOrderDirectDelivery = NoYes::No;
            }
        }
    }
}

/// <summary>
