public void clicked()
{
    super();

    purchParameters.PurchaseCycle = purchCycle_PurchaseCycle.value();
    purchParameters.write();

    if (purchCycle_PurchaseCycle.value())
    {
        element.updatePurchCycle(true);
        salesPurchCycle.createRecords(SalesPurchCycleModuleParameter::VendParameter);
    }
    else
    {
        element.updatePurchCycle(false);
        SalesPurchCycle::deleteSalesPurchCycle(SalesPurchCycleModuleParameter::VendParameter, '');
    }
    salesPurchCycle_ds.executeQuery();
}

public void clicked()
{
    if (PurchRFQCaseTableListPageInteraction::isRFQCaseFieldsTransferToReplyOrShowLegacyFieldsFeatureEnabled())
    {
        super();
    }
    else
    {
        warning(strFmt("@ProcurementAndSourcing:MessageToDisplayLegacyDefaultRFQReplyFields"));
    }
}

public boolean modified()
{
    boolean isValid = super();

    if (isValid)
    {                
        element.setAllowEditForBidType(!this.value());
    }

    return isValid;
}

[Hookable(false)]
public boolean modified()
{
    boolean ret = super();

    if (ret)
    {
        VendorQuestion_IsDefaultedToDirectVendorResponse.allowEdit(VendorQuestion_IsVendorQuestionEnabled.checked());

        if (!VendorQuestion_IsVendorQuestionEnabled.checked())
        {
            PurchRFQParameters.IsDefaultedToDirectVendorResponse = NoYes::No;
        }
    }

    return ret;
}

