   SuppItemAccountRelation accountRelation()
{
    switch (module)
    {
        case ModuleInventCustVend::Cust     :
            if (custTable.RecId)
            {
                return custTable.AccountNum;
            }

        case ModuleInventCustVend::Vend     :
            if (vendTable.RecId)
            {
                return vendTable.AccountNum;
            }
    }

    return '';
}

SuppItemGroupId  groupId()
{
    SuppItemGroupId suppItemGroupId;

    switch (fromModule)
    {
        case ModuleInventCustVend::Invent :
            if (inventTable)
            {
                suppItemGroupId = (module == ModuleInventCustVend::Cust ?
                                   inventTable.salesSuppItemGroupId()   :
                                   inventTable.purchSuppItemGroupId());
            }
            else
            {
                suppItemGroupId = suppItemGroup.GroupId;
            }
            break;

        case ModuleInventCustVend::Cust :
            suppItemGroupId = (custTable.RecId ? custTable.SuppItemGroupId : suppItemGroup.GroupId);
            break;

        case ModuleInventCustVend::Vend :
            suppItemGroupId = (vendTable.RecId ? vendTable.SuppItemGroupId : suppItemGroup.GroupId);
            break;
    }

    return suppItemGroupId;
}

public void  init()
{
    if (!element.args().record() || !element.args().parmEnumType() )
    {
        throw error("@SYS22539");
    }

    module = element.args().parmEnum();

    super();        

    element.design().caption(strfmt('%1 - %2',"@SYS58282",module));

    element.initParameters();
    element.initRelation();

    element.updateDesign(InventDimFormDesignUpdate::Init);
}

void  initCustTable()
{
    accountRelation = custTable.AccountNum;
}

