void  initInventTable()
{
    itemRelation = inventTable.ItemId;

    if (module == ModuleInventCustVend::Vend)
    {
        unitID = inventTable.purchUnitId();
    }
    else
    {
        unitID = inventTable.salesUnitId();
    }
}

void  initParameters()
{
    switch (element.args().dataset())
    {
        case tablenum(SalesTable) :
            salesTable      = element.args().record();
            fromModule      = ModuleInventCustVend::Cust;
            element.initSalesTable();
            break;

        case tablenum(CustTable) :
            custTable       = element.args().record();
            fromModule      = ModuleInventCustVend::Cust;
            element.initCustTable();
            break;

        case tablenum(PurchTable) :
            purchTable      = element.args().record();
            fromModule      = ModuleInventCustVend::Vend;
            element.initPurchTable();
            break;

        case tablenum(VendTable) :
            vendTable       = element.args().record();
            fromModule      = ModuleInventCustVend::Vend;
            element.initVendTable();
            break;

        case tablenum(InventTable) :
            inventTable     = element.args().record();
            fromModule      = ModuleInventCustVend::Invent;
            element.initInventTable();
            break;

        case tablenum(SuppItemGroup) :
            suppItemGroup   = element.args().record();
            fromModule      = suppItemGroup.Module;
            element.initSuppItemGroup();
            break;
    }
}

void  initPurchTable()
{
    vendTable       = purchTable.vendTable_OrderAccount();
    accountRelation = vendTable.AccountNum;
}

void  initRecord()
{
    if (suppItemTable.ItemCode == TableGroupAll::All)
    {
        suppItemTable_ds.object(fieldnum(SuppItemTable, ItemRelation)).skip(true);
        suppItemTable_ds.object(fieldnum(SuppItemTable, ItemRelation)).allowEdit(false);
        suppItemTable.ItemRelation = '';
    }
    else
    {
        suppItemTable_ds.object(fieldnum(SuppItemTable, ItemRelation)).skip(false);
        suppItemTable_ds.object(fieldnum(SuppItemTable, ItemRelation)).allowEdit(true);
    }

    if (suppItemTable.AccountCode == TableGroupAll::All)
    {
        suppItemTable_ds.object(fieldnum(SuppItemTable, AccountRelation)).skip(true);
        suppItemTable_ds.object(fieldnum(SuppItemTable, AccountRelation)).allowEdit(false);
        suppItemTable.AccountRelation = '';
    }
    else
    {
        suppItemTable_ds.object(fieldnum(SuppItemTable, AccountRelation)).skip(false);
        suppItemTable_ds.object(fieldnum(SuppItemTable, AccountRelation)).allowEdit(true);
    }
}

void  initRelation()
{
    suppItemTable_ds.object(fieldnum(SuppItemTable, ItemCode)).visible(true);
    suppItemTable_ds.object(fieldnum(SuppItemTable, ItemRelation)).visible(true);

    switch (fromModule)
    {
        case ModuleInventCustVend::Invent :
            relationCode.selection(itemCode);
            relationCode.label("@SYS58740");
            relation.helpText("@SYS69422");
            suppItemTable_ds.object(fieldnum(SuppItemTable, ItemCode)).visible(false);
            suppItemTable_ds.object(fieldnum(SuppItemTable, ItemRelation)).visible(false);
            break;

        case ModuleInventCustVend::Cust :
            relationCode.selection(accountCode);
            relationCode.label("@SYS12243");
            relation.helpText("@SYS69423");
            suppItemTable_ds.object(fieldnum(SuppItemTable, AccountCode)).visible(false);
            suppItemTable_ds.object(fieldnum(SuppItemTable, AccountRelation)).visible(false);
            break;

        case ModuleInventCustVend::Vend :
            relationCode.selection(accountCode);
            relationCode.label("@SYS12243");
            relation.helpText("@SYS69423");
            suppItemTable_ds.object(fieldnum(SuppItemTable, AccountCode)).visible(false);
            suppItemTable_ds.object(fieldnum(SuppItemTable, AccountRelation)).visible(false);
            break;
    }
}

