    accountRelation = vendTable.AccountNum;
}

InventDimCtrl_Frm inventDimSetupObject()
{
    return lookUpSupp ? inventDimFormSetupSuppItemInventDim : inventDimFormSetupInventDim;
}

ItemId itemId()
{
    // This method is used by the lookup's for inventory dimensions (via call-back)

    if (lookUpSupp)
        return suppItemTable.SuppItemId;
    else if (suppItemTable.ItemCode == TableGroupAll::Table)
        return suppItemTable.ItemRelation;
    return '';
}

void  reSelect()
{
    suppItemTable_ds.object(fieldnum(SuppItemTable, ItemCode)).allowEdit(false);
    suppItemTable_ds.object(fieldnum(SuppItemTable, ItemCode)).skip(true);

    suppItemTable_ds.object(fieldnum(SuppItemTable, ItemRelation)).allowEdit(true);
    suppItemTable_ds.object(fieldnum(SuppItemTable, ItemRelation)).skip(false);

    suppItemTable_ds.object(fieldnum(SuppItemTable, AccountCode)).allowEdit(false);
    suppItemTable_ds.object(fieldnum(SuppItemTable, AccountCode)).skip(true);

    suppItemTable_ds.object(fieldnum(SuppItemTable, AccountRelation)).allowEdit(true);
    suppItemTable_ds.object(fieldnum(SuppItemTable, AccountRelation)).skip(false);

    switch (fromModule)
    {
        case ModuleInventCustVend::Invent:
            switch (itemCode)
            {
                case TableGroupAll::Table :
                    itemRelation = inventTable.ItemId;
                    break;
                case TableGroupAll::GroupId :
                    itemRelation = element.groupId();
                    break;
                case TableGroupAll::All :
                    itemRelation = '';
                    break;
            }

            suppItemTable_ds.object(fieldnum(SuppItemTable, AccountCode)).allowEdit(true);
            suppItemTable_ds.object(fieldnum(SuppItemTable, AccountCode)).skip(false);
            accountCode = TableGroupAll::Table;
            break;

        default:
            switch (accountCode)
            {
                case TableGroupAll::Table :
                    accountRelation = element.accountRelation();
                     break;

                case TableGroupAll::GroupId :
                    accountRelation = element.groupId();
                    break;
                case TableGroupAll::All :
                    accountRelation = '';
                    break;
            }

            suppItemTable_ds.object(fieldnum(SuppItemTable, ItemCode)).allowEdit(true);
            suppItemTable_ds.object(fieldnum(SuppItemTable, ItemCode)).skip(false);
            itemCode = TableGroupAll::Table;
            break;
        }

    blockItemCode.status(RangeStatus::Open);
    blockItemRelation.status(RangeStatus::Open);
    blockAccountCode.status(RangeStatus::Open);
    blockAccountRelation.status(RangeStatus::Open);

    switch (fromModule)
    {
        case ModuleInventCustVend::Invent :
            relation.text(itemRelation);
            blockItemCode.value(queryValue(itemCode));
            blockItemCode.status(RangeStatus::Hidden);
            blockItemRelation.value(queryValue(itemRelation));
            blockItemRelation.status(RangeStatus::Hidden);
            break;

        default :
            relation.text(accountRelation);
            blockAccountCode.value(queryValue(accountCode));
            blockAccountCode.status(RangeStatus::Hidden);
            blockAccountRelation.value(queryValue(accountRelation));
            blockAccountRelation.status(RangeStatus::Hidden);
            break;
    }

    suppItemTable_ds.executeQuery();
    suppItemTable_ds.queryRun().saveUserSetup(false);
    suppItemTable_ds.refresh();
}

void  run()
{
    super();

    element.reSelect();
}

void updateDesign(InventDimFormDesignUpdate mode, boolean itemId = true)
{
    Set             validInventDim;
    Set             validSuppItemInventDim;
    InventDimParm   inventDimParmVisible;
    InventDimParm   suppItemInventDimParmVisible;
    InventDimParm   inventDimParmEnabled;
    InventDimParm   suppItemInventDimParmEnabled;

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init          :
        case InventDimFormDesignUpdate::LinkActive    :

            validInventDim = new Set(Types::String);
            validInventDim.add(inventDim_ds.name());

            validSuppItemInventDim = new Set(Types::String);
            validSuppItemInventDim.add(suppItemInventDim_ds.name());

            if (!inventDimFormSetupInventDim)
            {
                inventDimFormSetupInventDim  = InventDimCtrl::newFromFormValidDataSources(element, validInventDim);
                inventDimFormSetupInventDim.parmSkipOnHandLookUp(true);
            }

            if (fromModule ==  ModuleInventCustVend::Invent)
            {
                if (element.args().dataset() == tablenum(InventTable))
                {
                    inventDimParmVisible = inventTable.inventDimParmProductDimensions();
                }
                else
                {
                    inventDimParmVisible.clear();
                }
            }
            else
            {
                inventDimParmVisible.initProductDimensionsAllGroups();
            }

            inventDimFormSetupInventDim.parmDimParmVisibleGrid(inventDimParmVisible);

            if (!inventDimFormSetupSuppItemInventDim)
            {
                inventDimFormSetupSuppItemInventDim  = InventDimCtrl::newFromFormValidDataSources(element, validSuppItemInventDim);
                inventDimFormSetupSuppItemInventDim.parmSkipOnHandLookUp(true);
            }

            suppItemInventDimParmVisible.initProductDimensionsAllGroups();
            inventDimFormSetupSuppItemInventDim.parmDimParmVisibleGrid(suppItemInventDimParmVisible);

            // do continue
        case InventDimFormDesignUpdate::Active        :
            inventDimFormSetupInventDim.formActiveSetup();
            if (suppItemTable.ItemCode == TableGroupAll::Table)
            {
                inventDimParmEnabled = EcoResProductDimGroupSetup::newItemId(suppItemTable.ItemRelation).inventDimParmActiveDimensions();
            }
            else
            {
                inventDimParmEnabled.clear();
            }
            inventDimFormSetupInventDim.parmDimParmEnabled(inventDimParmEnabled);
            inventDimFormSetupInventDim.formSetControls(true);

            inventDimFormSetupSuppItemInventDim.formActiveSetup();
            suppItemInventDimParmEnabled = EcoResProductDimGroupSetup::newItemId(suppItemTable.SuppItemId).inventDimParmActiveDimensions();
            inventDimFormSetupSuppItemInventDim.parmDimParmEnabled(suppItemInventDimParmEnabled);
            inventDimFormSetupSuppItemInventDim.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange   :
            if (itemId)
            {
                inventDimFormSetupInventDim.formActiveSetup();
                if (suppItemTable.ItemCode == TableGroupAll::Table)
                {
                    inventDimParmEnabled = EcoResProductDimGroupSetup::newItemId(suppItemTable.ItemRelation).inventDimParmActiveDimensions();
                }
                else
                {
                    inventDimParmEnabled.clear();
                }
                inventDimFormSetupInventDim.parmDimParmEnabled(inventDimParmEnabled);
                inventDimFormSetupInventDim.formSetControls(false);
            }
            else
            {
                inventDimFormSetupSuppItemInventDim.formActiveSetup();
                suppItemInventDimParmEnabled = EcoResProductDimGroupSetup::newItemId(suppItemTable.SuppItemId).inventDimParmActiveDimensions();
                inventDimFormSetupSuppItemInventDim.parmDimParmEnabled(suppItemInventDimParmEnabled);
                inventDimFormSetupSuppItemInventDim.formSetControls(false);
            }
            break;

        default :
            throw error(strfmt("@SYS54195",funcname()));
    }
}

public boolean validateWrite()
{
    boolean ret;
    suppItemTable.InventDimId           = InventDim::findOrCreate(inventDim).InventDimId;
    suppItemTable.SuppItemInventDimId   = InventDim::findOrCreate(suppItemInventDim).InventDimId;

    ret = super();

    return ret;
}

public void write()
{
    suppItemTable.InventDimId           = InventDim::findOrCreate(inventDim).InventDimId;
    suppItemTable.SuppItemInventDimId   = InventDim::findOrCreate(suppItemInventDim).InventDimId;

    super();
}

public void init()
{
    QueryBuildDataSource queryBuildDataSource;

    super();

    queryBuildDataSource = this.query().dataSourceTable(tablenum(SuppItemTable));
    queryBuildDataSource.clearDynalinks();
    queryBuildDataSource.clearRanges();

    queryBuildDataSource.addRange(fieldnum(SuppItemTable, Module)).value(queryValue(module));
    queryBuildDataSource.rangeField(fieldnum(SuppItemTable, Module)).status(RangeStatus::Hidden);

    blockItemCode       = queryBuildDataSource.addRange(fieldnum(SuppItemTable, ItemCode));
    blockItemRelation   = queryBuildDataSource.addRange(fieldnum(SuppItemTable, ItemRelation));
    blockAccountCode    = queryBuildDataSource.addRange(fieldnum(SuppItemTable, AccountCode));
    blockAccountRelation= queryBuildDataSource.addRange(fieldnum(SuppItemTable, AccountRelation));
}

